frag:
precision mediump float;
uniform vec2 u_resolution;
uniform float u_time;

#define MAX_STEPS 100
#define MAX_DIST 100.0
#define SURF_DIST 0.001
#define PI 3.14159265359

// Basic shape SDFs
float sdSphere(vec3 p, float r) {
    return length(p) - r;
}

float sdBox(vec3 p, vec3 b) {
    vec3 d = abs(p) - b;
    return length(max(d,0.0)) + min(max(d.x,max(d.y,d.z)),0.0);
}

float sdCylinder(vec3 p, vec3 c) {
    return length(p.xz - c.xy) - c.z;
}

float sdPlane(vec3 p, vec3 n, float h) {
    return dot(p, normalize(n)) - h;
}

// Smooth min for blending
float smin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5*(b-a)/k, 0.,1.);
    return mix(b,a,h) - k*h*(1.-h);
}

// Domain repetition
float opRep1(float x, float c) {
    return mod(x, c) - c*0.5;
}

vec2 opRep2(vec2 p, vec2 c) {
    return mod(p, c) - c*0.5;
}

// Random noise
float hash(vec3 p) {
    p = fract(p * 0.3183099 + .1);
    p *= 17.0;
    return fract(p.x * p.y * p.z * (p.x + p.y + p.z));
}

float marbleNoise(vec3 p) {
    float n = 0.;
    float amp = 1.;
    for(int i=0; i<5; i++){
        n += amp * hash(p);
        p *= 2.;
        amp *= 0.5;
    }
    return n;
}

// Architectural elements
float sdArch(vec3 p, vec2 size, float radius) {
    float wall = sdBox(p, vec3(size.x, size.y, size.x));
    vec3 q = p - vec3(0.0, size.y - radius, 0.0);
    float cut = sdCylinder(q, vec3(0.0,0.0,radius));
    return max(wall, -cut);
}

// Gate animation functions
float gateAnimation(float time) {
    // Create smooth open-close animation
    float openAmount = sin(time * 0.5) * 0.5 + 0.5;
    // Add easing - slow at ends, fast in middle
    openAmount = smoothstep(0.0, 1.0, openAmount);
    return openAmount;
}

float sdGate(vec3 p, float openAmount) {
    // Each gate half
    float gateWidth = 2.5;
    float gateHeight = 3.0;
    float gateThickness = 0.2;

    // Left gate position with animation
    vec3 leftGatePos = p;
    leftGatePos.x += gateWidth * openAmount;
    float leftGate = sdBox(leftGatePos, vec3(gateWidth, gateHeight, gateThickness));

    // Right gate position with animation
    vec3 rightGatePos = p;
    rightGatePos.x -= gateWidth * openAmount;
    float rightGate = sdBox(rightGatePos, vec3(gateWidth, gateHeight, gateThickness));
    vec3 leftPatternPos = leftGatePos;
    leftPatternPos.z -= gateThickness * 0.5;
    leftPatternPos.y -= 0.5;
    vec3 rightPatternPos = rightGatePos;
    rightPatternPos.z -= gateThickness * 0.5;
    rightPatternPos.y -= 0.5;

    float pattern1 = sdBox(leftPatternPos, vec3(gateWidth * 10.8, gateHeight * 1.7, 0.05));
    float pattern2 = sdBox(rightPatternPos, vec3(gateWidth * 0.8, gateHeight * 0.7, 0.05));

    // Combine gates and patterns
    leftGate = max(leftGate, -pattern1);
    rightGate = max(rightGate, -pattern2);

    return min(leftGate, rightGate);
}

// Main scene
float sceneSDF(vec3 p) {
    // Floor
    float floorDist = p.y + 0.1;

    // Archways
    vec3 w = p;
    w.z += 5.0;
    w.x = opRep1(w.x, 4.0);
    w.y = opRep1(w.y, 4.0);
    float arch = sdArch(w, vec2(1.8, 2.0), 1.0);

    // Gates
    float openAmount = gateAnimation(u_time);
    float gates = sdGate(p - vec3(3.0, 1.5, 3.0), openAmount);

    // Add some columns beside the gate
    vec3 leftCol = p - vec3(-3.5, 0.0, 3.0);
    vec3 rightCol = p - vec3(3.5, 0.0, 3.0);
    float column1 = sdCylinder(leftCol, vec3(0.0, 0.0, 0.5));
    float column2 = sdCylinder(rightCol, vec3(0.0, 0.0, 0.5));

    // Combine everything
    float scene = min(floorDist, arch);
    scene = min(scene, gates);
    scene = min(scene, column1);
    scene = min(scene, column2);

    return scene;
}

// Raymarching function
float raymarch(vec3 ro, vec3 rd) {
    float t = 0.0;
    for(int i=0; i<MAX_STEPS; i++){
        vec3 pos = ro + rd*t;
        float d = sceneSDF(pos);
        t += d;
        if(d < SURF_DIST || t > MAX_DIST) break;
    }
    return t;
}

// Normal calculation
vec3 getNormal(vec3 p) {
    float e = 0.001;
    return normalize(vec3(
        sceneSDF(p + vec3(e,0,0)) - sceneSDF(p - vec3(e,0,0)),
        sceneSDF(p + vec3(0,e,0)) - sceneSDF(p - vec3(0,e,0)),
        sceneSDF(p + vec3(0,0,e)) - sceneSDF(p - vec3(0,0,e))
    ));
}

// Materials
vec3 getMaterial(vec3 p, vec3 n) {
    // Marble-like pattern for walls and floor
    vec3 white = vec3(0.95);
    vec3 gray = vec3(0.7);
    float v = sin((p.x + p.y*0.5)*10.0 + marbleNoise(p)*4.0);
    v = smoothstep(-0.2, 0.2, v);
    vec3 marbleMat = mix(white, gray, v);

    // Gold material for gates
    vec3 goldColor = vec3(1.0, 0.8, 0.2);

    // Check if we're on the gates
    vec3 gatePos = p - vec3(0.0, 2.5, 3.0);
    float openAmount = gateAnimation(u_time);
    float gateCheck = sdGate(gatePos, openAmount);

    // If close to gate surface
    if (gateCheck < 0.1) {
        // Add some variation to the gold
        float pattern = sin(p.x * 2.0) * sin(p.y * 20.0) * 0.5 + 0.5;
        return mix(goldColor, goldColor * 0.7, pattern);
    }

    // Column material (polished stone)
    vec3 columnPos1 = p - vec3(-3.5, 0.0, 3.0);
    vec3 columnPos2 = p - vec3(3.5, 0.0, 3.0);
    float column1 = sdCylinder(columnPos1, vec3(0.0, 0.0, 0.5));
    float column2 = sdCylinder(columnPos2, vec3(0.0, 0.0, 0.5));

    if (column1 < 0.1 || column2 < 0.1) {
        // Polished white stone for columns
        return vec3(0.9, 0.9, 0.95);
    }

    // Default is marble
    return marbleMat;
}

// Shadows
float getShadow(vec3 p, vec3 ldir) {
    float res = 1.0;
    float t = 0.01;
    for(int i=0; i<16; i++){
        float h = sceneSDF(p + ldir*t);
        if(h<0.001) return 0.0;
        res = min(res, 16.0*h/t);
        t += h;
        if(t>20.0) break;
    }
    return res;
}

// Atmospheric fog effect
vec3 applyFog(vec3 col, float dist, vec3 rd) {
    vec3 fogC = vec3(0.75, 0.8, 0.85);
    float f = 1.0 - exp(-dist*0.02);
    f *= smoothstep(0.0, 1.0, rd.y*0.5+0.5);
    return mix(col, fogC, f);
}

// Main function
void main() {
    // Normalized pixel coordinates
    vec2 uv = (gl_FragCoord.xy / u_resolution)*2.0 - 1.0;
    uv.x *= u_resolution.x/u_resolution.y;

    // Camera setup
    float cameraSpeed = 0.3;
    float camX = sin(u_time * cameraSpeed) * 2.0;
    vec3 ro = vec3(camX, 2.5, 8.0); // Slightly moving camera
    vec3 ta = vec3(0.0, 1.0, 0.0);

    // Camera frame
    vec3 fw = normalize(ta - ro);
    vec3 rt = normalize(cross(vec3(0,1,0), fw));
    vec3 up = cross(fw, rt);

    // Field of view
    float fov = PI/3.0;
    vec3 rd = normalize(fw + uv.x*rt*tan(fov/2.0) + uv.y*up*tan(fov/2.0));

    // Raymarching
    float t = raymarch(ro, rd);

    // Sky color gradient
    vec3 col = mix(vec3(0.6,0.7,0.8), vec3(0.3,0.4,0.6), rd.y*0.5+0.5);

    // If we hit something
    if(t < MAX_DIST) {
        vec3 pos = ro + rd*t;
        vec3 n = getNormal(pos);
        vec3 mat = getMaterial(pos, n);

        // Lighting
        vec3 light1 = normalize(vec3(5, 10, 2));
        vec3 light2 = normalize(vec3(-3, 8, -2));

        // Diffuse lighting from two light sources
        float diff1 = max(dot(n, light1), 0.0) * getShadow(pos + n*0.01, light1);
        float diff2 = max(dot(n, light2), 0.0) * getShadow(pos + n*0.01, light2);

        // Ambient light
        vec3 amb = vec3(0.2);

        // Combine lighting
        vec3 lit = mat * (amb + diff1 * vec3(1.0, 0.9, 0.8) + diff2 * vec3(0.8, 0.9, 1.0));

        // Add specular highlight
        vec3 ref = reflect(rd, n);
        float spec = pow(max(dot(ref, light1), 0.0), 32.0);
        lit += vec3(1.0) * spec * 0.5;

        // Apply fog for depth
        col = applyFog(lit, t, rd);
    }

    // Output to screen
    gl_FragColor = vec4(col, 1.0);
}

vert:
attribute vec2 a_position;
void main() {
  gl_Position = vec4(a_position, 0., 1.);
}

frag:
precision mediump float;
uniform vec2 u_resolution;
uniform float u_time;

// Constants
const int MAX_STEPS = 100;
const float MAX_DIST = 20.0;
const float SURF_DIST = 0.001;
const float PI = 3.14159265359;

// Rotation matrix around the Y axis
mat3 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, 0, s),
        vec3(0, 1, 0),
        vec3(-s, 0, c)
    );
}

// Rotation matrix around the X axis
mat3 rotateX(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(1, 0, 0),
        vec3(0, c, -s),
        vec3(0, s, c)
    );
}

// Smooth minimum function for soft blending
float smin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5 * (b - a) / k, 0.0, 1.0);
    return mix(b, a, h) - k * h * (1.0 - h);
}

// SDF for a sphere
float sdSphere(vec3 p, float r) {
    return length(p) - r;
}

// SDF for a box
float sdBox(vec3 p, vec3 b) {
    vec3 d = abs(p) - b;
    return min(max(d.x, max(d.y, d.z)), 0.0) + length(max(d, 0.0));
}

// SDF for a melting object (Dali-style)
float sdMeltingClock(vec3 p, float time) {
    // Clock base shape
    vec3 clockPos = p - vec3(0.0, sin(time * 0.4) * 0.2 + 1.0, 0.0);
    float clockRadius = 1.2;
    float clock = sdSphere(clockPos, clockRadius);

    // Melting effect - distort the sphere
    float meltFactor = max(0.0, p.y - sin(p.x * 2.0 + time) * 0.3);
    clock += sin(p.x * 3.0 + time) * sin(p.z * 3.0) * 0.1;
    clock -= meltFactor * 0.4;

    return clock;
}

// SDF for tree-like structure
float sdTree(vec3 p) {
    // Trunk
    vec3 trunkPos = p;
    trunkPos.y -= 1.5;
    float trunk = sdBox(trunkPos, vec3(0.2, 1.5, 0.2));

    // Bendy branches - Dali style
    vec3 branchPos = p - vec3(0.0, 0.2, 0.0);
    float branch1 = sdBox(rotateY(0.6) * (branchPos - vec3(0.4, 0.0, 0.0)), vec3(0.6, 0.1, 0.1));
    float branch2 = sdBox(rotateY(-0.4) * (branchPos - vec3(-0.3, 0.3, 0.0)), vec3(0.5, 0.1, 0.1));

    // Combine with smooth minimum
    float branches = smin(branch1, branch2, 0.3);
    return smin(trunk, branches, 0.2);
}

// SDF for a plane with ripples
float sdWavyPlane(vec3 p, float time) {
    // Create wavy ground with distortion
    float y = sin(p.x * 0.8 + time * 0.3) * 0.2 + sin(p.z * 1.2 + time * 0.2) * 0.2;
    return p.y - y - (-2.0); // -2.0 is the basic height
}

// Scene SDF - combines all objects
float sceneSDF(vec3 p) {
    // Animate and rotate the entire scene slightly
    vec3 sp = rotateY(sin(u_time * 0.1) * 0.2) * p;

    // Melting clock - classic Dali
    float clock = sdMeltingClock(sp - vec3(-2.0, 0.3, 1.0), u_time);

    // Surreal tree
    float tree = sdTree(sp - vec3(2.5, 0.0, 1.0));

    // Another melting object
    vec3 meltObjPos = sp - vec3(1.0, -0.5, -1.0);
    meltObjPos = rotateX(sin(u_time * 0.3) * 0.5) * meltObjPos;
    float meltObj = sdBox(meltObjPos, vec3(0.7, 0.2, 0.7));
    meltObj -= sin(meltObjPos.x * 5.0) * sin(meltObjPos.z * 5.0) * 0.1;

    // Floating sphere
    float floatingSphere = sdSphere(sp - vec3(0.0, sin(u_time * 0.5) * 0.5 + 1.8, -1.5), 0.4);

    // Ground plane with waves
    float ground = sdWavyPlane(sp, u_time);

    // Combine objects
    float scene = smin(clock, ground, 0.3);
    scene = smin(scene, tree, 0.3);
    scene = smin(scene, meltObj, 0.3);
    scene = smin(scene, floatingSphere, 0.3);

    return scene;
}

// Ray marching function
float raymarch(vec3 ro, vec3 rd) {
    float dO = 0.0;

    for(int i = 0; i < MAX_STEPS; i++) {
        vec3 p = ro + rd * dO;
        float dS = sceneSDF(p);
        dO += dS;
        if(dO > MAX_DIST || dS < SURF_DIST) break;
    }

    return dO;
}

// Calculate normal using gradient sampling
vec3 getNormal(vec3 p) {
    float d = sceneSDF(p);
    vec2 e = vec2(0.001, 0.0);

    vec3 n = d - vec3(
        sceneSDF(p - e.xyy),
        sceneSDF(p - e.yxy),
        sceneSDF(p - e.yyx)
    );

    return normalize(n);
}

// Get material color based on position
vec3 getMaterial(vec3 p) {
    // Desert sand color for ground
    vec3 sandColor = vec3(0.8, 0.7, 0.5);

    // Clock color - goldish
    vec3 clockColor = vec3(0.9, 0.8, 0.4);

    // Tree color - dark browns
    vec3 treeColor = vec3(0.4, 0.3, 0.2);

    // Melting object color - bluish
    vec3 meltColor = vec3(0.3, 0.5, 0.8);

    // Determine which object we're coloring
    vec3 sp = rotateY(sin(u_time * 0.1) * 0.2) * p;

    // Distance to each object
    float dClock = sdMeltingClock(sp - vec3(-2.0, 0.3, 1.0), u_time);
    float dTree = sdTree(sp - vec3(2.5, 0.0, 1.0));

    vec3 meltObjPos = sp - vec3(1.0, -0.5, -1.0);
    meltObjPos = rotateX(sin(u_time * 0.3) * 0.5) * meltObjPos;
    float dMeltObj = sdBox(meltObjPos, vec3(0.7, 0.2, 0.7));

    float dSphere = sdSphere(sp - vec3(0.0, sin(u_time * 0.5) * 0.5 + 1.8, -1.5), 0.4);

    // Find the closest object
    float minDist = min(min(min(dClock, dTree), dMeltObj), dSphere);

    // Create surrealistic color palette
    vec3 color;

    if(minDist == dClock) {
        color = clockColor;
        // Add some variation to the clock
        color += sin(p.x * 10.0) * 0.1;
    } else if(minDist == dTree) {
        color = treeColor;
        // Add bark-like texture
        color += sin(p.y * 20.0) * 0.05;
    } else if(minDist == dMeltObj) {
        color = meltColor;
        // Add some psychedelic patterns
        color += sin(p.x * 15.0) * sin(p.z * 15.0) * 0.1;
    } else if(minDist == dSphere) {
        // Reflective sphere
        color = vec3(0.9);
        color *= sin(p.x * 10.0 + u_time) * 0.2 + 0.8;
    } else {
        // Must be ground
        color = sandColor;
        // Add some subtle desert patterns
        color += sin(p.x * 3.0) * sin(p.z * 3.0) * 0.05;
    }

    return color;
}

// Main rendering function
void main() {
    vec2 uv = gl_FragCoord.xy / u_resolution;
    uv = uv * 2.0 - 1.0;
    uv.x *= u_resolution.x / u_resolution.y;

    // Camera setup - slightly tilted perspective for surreal effect
    float camAngle = sin(u_time * 0.1) * 0.2;
    vec3 ro = vec3(0.0, 0.5, -5.0); // Ray origin (camera position)
    vec3 rd = normalize(vec3(uv, 1.5)); // Ray direction with slight fish-eye

    // Apply slight camera rotation
    rd = rotateY(camAngle) * rd;

    // Sky gradient - Dali-esque sky
    vec3 skyColor = mix(
        vec3(0.6, 0.8, 0.9), // Light blue 
        vec3(0.9, 0.7, 0.5), // Yellowish sunset
        pow(1.0 - max(rd.y, 0.0), 2.0)
    );

    // Add subtle sun in the sky
    float sun = pow(max(dot(rd, normalize(vec3(0.3, 0.5, 0.5))), 0.0), 32.0);
    skyColor += vec3(1.0, 0.8, 0.4) * sun;

    // Raymarch the scene
    float d = raymarch(ro, rd);

    // Final color
    vec3 col = skyColor;

    // Object rendering
    if(d < MAX_DIST) {
        // Hit position
        vec3 p = ro + rd * d;

        // Surface normal
        vec3 n = getNormal(p);

        // Light direction and position
        vec3 lightPos = vec3(2.0, 5.0, -3.0);
        vec3 lightDir = normalize(lightPos - p);

        // Material color
        vec3 matCol = getMaterial(p);

        // Ambient light
        float ambInt = 0.2;

        // Diffuse light
        float diff = max(dot(n, lightDir), 0.0);
        diff = pow(diff, 0.8); // Soften the diffuse

        // Specular light - for reflective surfaces
        vec3 reflectDir = reflect(-lightDir, n);
        float spec = pow(max(dot(rd, reflectDir), 0.0), 8.0);
        float specInt = 0.5;

        // Soft shadows
        float shadow = 1.0;

        // Combine lighting
        col = matCol * (ambInt + diff * shadow) + spec * specInt * shadow;

        // Distance fog - creates depth and atmosphere
        col = mix(col, skyColor, 1.0 - exp(-0.02 * d * d));
    }

    // Apply slight vignette effect for dramatic composition
    vec2 q = gl_FragCoord.xy / u_resolution;
    col *= 0.5 + 0.5 * pow(16.0 * q.x * q.y * (1.0 - q.x) * (1.0 - q.y), 0.2);

    // Output the final color
    gl_FragColor = vec4(col, 1.0);
}

