frag:
precision mediump float;
uniform vec2 u_resolution;
uniform float u_time;

const int MAX_STEPS = 40;
const float MAX_DIST = 25.0;
const float SURF_DIST = 0.002;

// 2D rotation
mat2 rot2(float a){ float c = cos(a), s = sin(a); return mat2(c, -s, s, c); }

// Warp helper
vec3 warp(vec3 p, float amp){
    p.xz += vec2(
        sin(p.y * 1.7 + u_time * 0.6),
        cos(p.y * 1.4 + u_time * 1.0)
    ) * amp;
    return p;
}

// SDFs
float sdDisc(vec3 p, float r, float h){
    vec2 d = vec2(length(p.xy) - r, abs(p.z) - h);
    return length(max(d, 0.0)) + min(max(d.x, d.y), 0.0);
}
float sdRoundedBox(vec3 p, vec3 b, float rad){
    vec3 q = abs(p) - b;
    return length(max(q, 0.0)) - rad + min(max(q.x, max(q.y, q.z)), 0.0);
}
float sdGround(vec3 p){
    float y = sin(p.x * 0.6 + u_time * 0.4) * 0.3
            + sin(p.z * 0.8 + u_time * 0.3) * 0.3;
    return p.y - (-1.5 + y);
}
// A looping “ribbon” arc
float sdRibbon(vec3 p){
    vec3 q = p - vec3(0.0, 0.2, 7.0);
    float angle = atan(q.z, q.x) + u_time * 0.5;
    float radius = 2.5 + sin(angle * 3.0 + u_time) * 0.1;
    vec2 d = vec2(length(q.xz) - radius, abs(q.y) - 0.05);
    return length(max(d, 0.0)) + min(max(d.x, d.y), 0.0);
}
// Floating paint blot (thin octahedron)
float sdOcta(vec3 p, float s){
    p = abs(p);
    return (p.x + p.y + p.z - s) / sqrt(3.0);
}

// Combined scene SDF + material ID
// IDs: 0=ground,1=clock,2=left pillar,3=right pillar,4=ribbon,5=blot
vec2 scene(vec3 p){
    float d = 1e5;
    float id = 0.0;
    // ground
    float g = sdGround(p);
    if(g < d){ d = g; id = 0.0; }
    // melting clock
    vec3 cp = warp(p - vec3(0.0, 0.5, 4.0), 0.25);
    float c = sdDisc(cp, 1.2, 0.04);
    if(c < d){ d = c; id = 1.0; }
    // left pillar
    vec3 lp = warp(p - vec3(-2.5, -0.5, 5.0), 0.1);
    float l = sdRoundedBox(lp, vec3(0.2, 1.8, 0.2), 0.05);
    if(l < d){ d = l; id = 2.0; }
    // right pillar
    vec3 rp = warp(p - vec3(2.5, -0.5, 6.0), 0.1);
    float r = sdRoundedBox(rp, vec3(0.15, 2.0, 0.15), 0.04);
    if(r < d){ d = r; id = 3.0; }
    // twisting ribbon
    float rb = sdRibbon(p);
    if(rb < d){ d = rb; id = 4.0; }
    // floating paint blot
    vec3 bp = p - vec3(0.0, 2.5, 5.5);
    bp.x += sin(u_time * 1.3) * 0.3;
    bp.z += cos(u_time * 1.1) * 0.3;
    float bl = sdOcta(bp, 0.3);
    if(bl < d){ d = bl; id = 5.0; }
    return vec2(d, id);
}

// Normal
vec3 getNormal(vec3 p){
    float e = 0.001;
    return normalize(vec3(
        scene(p + vec3(e, 0.0, 0.0)).x - scene(p - vec3(e, 0.0, 0.0)).x,
        scene(p + vec3(0.0, e, 0.0)).x - scene(p - vec3(0.0, e, 0.0)).x,
        scene(p + vec3(0.0, 0.0, e)).x - scene(p - vec3(0.0, 0.0, e)).x
    ));
}

// Ray-march -> (distance, matID)
vec2 rayMarch(vec3 ro, vec3 rd){
    float t = 0.0;
    float m = 0.0;
    for(int i = 0; i < MAX_STEPS; i++){
        vec3 pos = ro + rd * t;
        vec2 s = scene(pos);
        if(s.x < SURF_DIST || t > MAX_DIST){
            m = s.y;
            break;
        }
        t += s.x;
    }
    return vec2(t, m);
}

// Sky with sun disk and shafts
vec3 skyColor(vec3 rd){
    vec3 col = mix(vec3(0.96,0.82,0.62), vec3(0.48,0.36,0.28), rd.y * 0.5 + 0.5);
    float sun = smoothstep(0.995, 1.0, dot(rd, normalize(vec3(0.3,0.7,0.2))));
    col += vec3(1.0,0.9,0.6) * sun * 0.8;
    float shafts = pow(abs(sin(atan(rd.y, rd.x) * 30.0 + u_time * 2.0)), 20.0);
    col += vec3(1.0,0.8,0.5) * shafts * 0.02;
    return col;
}

void main(){
    vec2 uv = (gl_FragCoord.xy * 2.0 - u_resolution) / u_resolution.y;
    vec3 ro = vec3(0.0, 1.0 + sin(u_time * 0.3) * 0.15, -6.0);
    float sway = sin(u_time * 0.2) * 0.08;
    mat2 R = rot2(sway);
    vec3 rd = normalize(vec3(R * uv, 1.0));

    vec2 rm = rayMarch(ro, rd);
    float t = rm.x, id = rm.y;
    vec3 col = skyColor(rd);

    if(t < MAX_DIST){
        vec3 pos = ro + rd * t;
        vec3 n   = getNormal(pos);
        vec3 Lpos = vec3(5.0 * cos(u_time * 0.5), 5.0, 5.0 * sin(u_time * 0.5));
        vec3 L = normalize(Lpos - pos), V = normalize(ro - pos), H = normalize(L + V);
        float diff = max(dot(n, L), 0.0);
        float spec = pow(max(dot(n, H), 0.0), 24.0);

        vec3 base;
        if(id < 0.5)      base = vec3(0.8,0.75,0.5);
        else if(id < 1.5) base = vec3(1.0,0.95,0.3);
        else if(id < 2.5) base = vec3(0.9,0.8,0.5);
        else if(id < 3.5) base = vec3(0.85,0.75,0.4);
        else if(id < 4.5) base = vec3(0.7,0.4,0.6);
        else              base = vec3(0.3,0.6,0.4);

        vec3 amb  = base * 0.2;
        vec3 dif  = base * diff;
        vec3 spc  = vec3(1.0) * spec * 0.3;
        float amt = pow(1.0 - dot(V, n), 3.0) * 0.2;
        vec3 Rr   = reflect(rd, n), refl = skyColor(Rr);

        vec3 lit = amb + dif + spc;
        lit = mix(lit, refl, amt);

        float fog = 1.0 - exp(-0.02 * t);
        col = mix(lit, skyColor(rd), fog);
    }

    // Vignette (16.0 now a float)
    vec2 q = gl_FragCoord.xy / u_resolution;
    col *= 0.6 + 0.4 * pow(16.0 * q.x * q.y * (1.0 - q.x) * (1.0 - q.y), 0.2);

    gl_FragColor = vec4(col, 1.0);
}

