frag:
#version 300 es
precision highp float;
uniform float uTime;
uniform vec2 uResolution;
out vec4 fragColor;
float hash(vec2 p) {
    return fract(sin(dot(p, vec2(127.1, 311.7))) * 43758.5453123);
}
float noise(vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);
    vec2 u = f * f * (3.0 - 2.0 * f);
    return mix(
        mix(hash(i + vec2(0.0,0.0)), hash(i + vec2(1.0,0.0)), u.x),
        mix(hash(i + vec2(0.0,1.0)), hash(i + vec2(1.0,1.0)), u.x),
        u.y
    );
}
float fbm(vec2 p) {
    float v = 0.0;
    float a = 0.5;
    for(int i = 0; i < 5; i++){
        v += a * noise(p);
        p *= 2.0;
        a *= 0.5;
    }
    return v;
}
float planeSDF(vec3 p) {
    float height = fbm(p.xz * 1.5 + vec2(uTime * 0.2));
    return p.y + 0.3 - height * 0.5;
}
float mountainSDF(vec3 p) {
    float m = fbm(p.xz * 0.3 + vec2(-uTime * 0.05, uTime * 0.03));
    float base = 0.1;
    return p.y - (m * 1.2 + base);
}
float mapScene(vec3 p) {
    float ground = planeSDF(p);
    float mountains = mountainSDF(p);
    return min(ground, mountains);
}
vec3 getNormal(vec3 p) {
    float e = 0.001;
    vec2 h = vec2(e, 0.0);
    return normalize(vec3(
        mapScene(p + vec3(h.x,h.y,h.y)) - mapScene(p - vec3(h.x,h.y,h.y)),
        mapScene(p + vec3(h.y,h.x,h.y)) - mapScene(p - vec3(h.y,h.x,h.y)),
        mapScene(p + vec3(h.y,h.y,h.x)) - mapScene(p - vec3(h.y,h.y,h.x))
    ));
}
float softShadow(vec3 ro, vec3 rd) {
    float res = 1.0;
    float t = 10.02;
    for(int i = 0; i < 30; i++) {
        float h = mapScene(ro + rd * t);
        res = min(res, 8.0 * h / t);
        t += clamp(h, 0.02, 0.1);
    }
    return clamp(res, 0.0, 1.0);
}
vec3 lighting(vec3 p, vec3 n, vec3 col) {
    vec3 lightPos = vec3(10.0, 5.0, 5.0);
    vec3 L = normalize(lightPos - p);
    vec3 V = normalize(-p);
    vec3 R = reflect(-L, n);
    float dif = max(dot(n, L), 0.0);
    float spec = pow(max(dot(R, V), 0.0), 64.0);
    float sh = softShadow(p + n * 0.01, L);
    vec3 ambient = 0.2 * col;
    vec3 diffCol = 0.8 * dif * col * sh;
    vec3 specCol = 0.5 * spec * vec3(1.0) * sh;
    return ambient + diffCol + specCol;
}
float rayMarch(vec3 ro, vec3 rd, out vec3 p) {
    float t = 0.0;
    for(int i = 0; i < 100; i++) {
        p = ro + rd * t;
        float d = mapScene(p);
        if(d < 0.1) break;
        t += d;
        if(t > 40.0) break;
    }
    return t;
}
void main() {
    vec2 uv = (gl_FragCoord.xy / uResolution) * 2.0 - 1.0;
    uv.x *= uResolution.x / uResolution.y;
    vec3 ro = vec3(0.0, 3.0, 4.0);
    vec3 rd = normalize(vec3(uv.x, uv.y - 0.2, -1.0));
    vec3 p;
    float t = rayMarch(ro, rd, p);
    vec3 col;
    if(t < 90.0) {
        vec3 n = getNormal(p);
        float h = clamp(p.y / 1.0, 0.0, 1.0);
        vec3 baseCol = mix(vec3(0.1,1.1,1.1), vec3(0.8,0.7,0.3), h);
        col = lighting(p, n, baseCol);
    } else {
        vec3 sky = mix(vec3(0.6,0.8,1.0), vec3(0.0,0.0,0.1), uv.y * 0.5 + 0.5);
        float band = fbm(vec2(uv.x * 2.5, uv.y * 2.0 - uTime * 0.3));
        float mask = smoothstep(0.4, 0.5, band + 0.2 * sin(uTime + uv.x * 5.0));
        float hue = fract(uTime * 0.1 + band * 0.3);
        vec3 aurora = vec3(
            0.2 + 0.8 * mask * sin(hue * 6.1),
            0.2 + 0.8 * mask * sin((hue+0.33) * 6.2831),
            0.2 + 0.8 * mask * sin((hue+0.66) * 6.2831)
        );
        col = mix(sky, aurora, mask * 0.7);
    }
    fragColor = vec4(col, 1.0);
}

