               mix(hash(i + vec2(0,1)), hash(i + vec2(1,1)), u.x), u.y);
}
float polarPattern(vec2 uv, float t) {
    float r = length(uv);
    float a = atan(uv.y, uv.x);
    return sin(20.0 * r + 6.0 * a + sin(t * 0.3 + r * 4.0));
}
float gridPattern(vec2 uv) {
    uv *= 10.0;
    uv += sin(uv.yx + u_time * 0.2) * 0.4;
    vec2 gv = fract(uv) - 0.5;
    vec2 id = floor(uv);
    float d = length(gv);
    float cellNoise = hash(id);
    return smoothstep(0.2 + 0.3 * sin(cellNoise * 6.28), 0.0, d);
}
float moire(vec2 uv, float t) {
    float lines = sin(uv.x * 100.0 + sin(t)) + sin(uv.y * 100.0 + t * 1.3);
    return 0.5 + 0.5 * cos(lines + t * 0.5);
}
vec3 palette(float t) {
    return 0.5 + 0.5 * cos(2.0 * PI * (vec3(0.7, 0.4, 0.1) + t));
