// Complex polar tiling with recursive symmetry
float pattern(vec2 uv, float t) {
    float r = length(uv);
    float a = atan(uv.y, uv.x);
    float f = 0.0;

    // Multi-frequency concentric interference
    f += sin(15.0 * r - t * 1.3);
    f += sin(8.0 * r + 3.0 * a + t * 0.6);
    f += sin(20.0 * a + t * 2.0 + sin(r * 10.0));
    f += cos(5.0 * r * sin(3.0 * a + t));

    return f * 0.25;
}

// Glowing ring halo
float halo(vec2 uv, float time) {
    float r = length(uv);
    return smoothstep(0.2, 0.05, abs(r - 0.5 + 0.1 * sin(time * 0.8)));
}

// Velvet-soft palette
vec3 palette(float t) {
    vec3 c1 = vec3(0.8, 0.5, 0.3);
    vec3 c2 = vec3(0.2, 0.6, 0.9);
    vec3 c3 = vec3(0.1, 0.0, 0.1);
    return mix(c3, mix(c1, c2, 0.5 + 0.5 * sin(t)), 0.7 + 0.3 * cos(t * 1.5));
}

void main() {
    vec2 uv = (gl_FragCoord.xy - 0.5 * u_resolution.xy) / u_resolution.y;
    float t = u_time;

    // Slight distortion for fluidity
    uv += 0.05 * sin(uv.yx * 8.0 + t * 1.2);

    float p = pattern(uv, t);
    float h = halo(uv, t);
    float mask = smoothstep(-1.0, 1.0, p);

    vec3 col = palette(p + t * 0.1);
    col *= mask;
    col += vec3(h * 0.7, h * 0.4, h);

