#ifdef GL_ES
precision mediump float;
#endif

uniform vec2 u_resolution;
uniform float u_time;

#define PI 3.14159265359

// Normalize UV space
vec2 normalizeUV(vec2 uv) {
  return (uv * 2.0 - u_resolution) / min(u_resolution.x, u_resolution.y);
}

// Cosine color palette
vec3 palette(float t) {
  vec3 a = vec3(0.5);
  vec3 b = vec3(0.5);
  vec3 c = vec3(1.0, 0.8, 0.6);
  vec3 d = vec3(0.1, 0.3, 0.6);
  return a + b * cos(2.0 * PI * (c * t + d));
}

// Hexagonal tiling
vec2 hexTile(vec2 uv) {
  uv.y /= 0.866;             // sqrt(3)/2
  uv.x -= uv.y * 0.5;
  return uv;
}

// Hex cell distance with edge feathering
float hexDist(vec2 uv) {
  vec2 h = fract(hexTile(uv)) - 0.5;
  float d = length(h);
  return smoothstep(0.45, 0.5, d);
}

void main() {
  vec2 uv = normalizeUV(gl_FragCoord.xy) * 4.0;

  // Animate the UV field
  float t = u_time * 0.5;
  uv += vec2(sin(uv.y * 2.0 + t), cos(uv.x * 2.0 + t)) * 0.3;

  float mask = 1.0 - hexDist(uv);

  // Add a ripple glow effect
  float glow = sin(10.0 * length(fract(hexTile(uv)) - 0.5) - t * 3.0) * 0.5 + 0.5;
  float intensity = mask * glow;

  // Color based on distance and time
  vec3 color = palette(length(uv) * 0.2 + t + intensity);

  // Apply glow intensity
  color *= intensity;

  gl_FragColor = vec4(color, 1.0);
}

