frag:
#ifdef GL_ES
precision mediump float;
#endif

uniform vec2 u_resolution;
uniform float u_time;

// Repeat operator
vec3 opRepeat(vec3 p, float g) {
    return mod(p + 0.5 * g, g) - 0.5 * g;
}

// Signed distance for grid of thin walls
float sdGrid(vec3 p) {
    float thickness = 0.05;
    vec3 d = abs(opRepeat(p, 2.0)) - vec3(thickness);
    float xy = max(d.x, d.y);
    float yz = max(d.y, d.z);
    float zx = max(d.z, d.x);
    return min(min(xy, yz), zx);
}

// Estimate normal by central differences
vec3 estimateNormal(vec3 p) {
    float h = 0.001;
    return normalize(vec3(
        sdGrid(p + vec3(h, 0.0, 0.0)) - sdGrid(p - vec3(h, 0.0, 0.0)),
        sdGrid(p + vec3(0.0, h, 0.0)) - sdGrid(p - vec3(0.0, h, 0.0)),
        sdGrid(p + vec3(0.0, 0.0, h)) - sdGrid(p - vec3(0.0, 0.0, h))
    ));
}

// Ambient occlusion approximation
float ambientOcclusion(vec3 p, vec3 n) {
    float ao = 0.0;
    float sca = 1.0;
    for (int i = 1; i <= 5; i++) {
        float h = 0.1 * float(i);
        float d = sdGrid(p + n * h);
        ao += (h - d) * sca;
        sca *= 0.5;
    }
    return clamp(1.0 - ao, 0.0, 1.0);
}

// Ray marching returns distance and a glow factor
vec2 march(vec3 ro, vec3 rd) {
    float t = 0.0;
    const float MAX_D = 30.0;
    const int STEPS = 100;
    float glow = 0.0;
    for (int i = 0; i < STEPS; i++) {
        vec3 p = ro + rd * t;
        float d = sdGrid(p);
        if (d < 0.001) {
            glow = float(i) / float(STEPS);
            break;
        }
        t += d * 0.8;
        if (t > MAX_D) break;
    }
    return vec2(t, glow);
}

void main() {
    // Normalized pixel coordinates
    vec2 uv = (gl_FragCoord.xy * 2.0 - u_resolution.xy) / min(u_resolution.x, u_resolution.y);

    // Camera orbit
    float angle = u_time * 0.3;
    vec3 ro = vec3(sin(angle) * 5.0, cos(u_time * 0.2) * 2.0, cos(angle) * 5.0);
    vec3 ta = vec3(0.0);
    vec3 fw = normalize(ta - ro);
    vec3 rt = normalize(cross(vec3(0.0, 1.0, 0.0), fw));
    vec3 up = cross(fw, rt);
    vec3 rd = normalize(fw + uv.x * rt + uv.y * up);

    // Ray march: x=distance, y=glow factor
    vec2 res = march(ro, rd);
    float t = res.x;
    float glowFactor = res.y;

    vec3 col = vec3(0.0);
    if (t < 29.9) {
        // Surface point and normal
        vec3 pos = ro + rd * t;
        vec3 n = estimateNormal(pos);

        // Lighting
        vec3 lightDir = normalize(vec3(sin(u_time), 1.0, cos(u_time * 0.5)));
        float diff = max(dot(n, lightDir), 0.0);
        float spec = pow(max(dot(reflect(-lightDir, n), -rd), 0.0), 16.0);

        // Ambient occlusion
        float ao = ambientOcclusion(pos, n);

        // Base color animated
        vec3 baseColor = 0.5 + 0.5 * cos(u_time + pos * 1.0 + vec3(0.0, 2.0, 4.0));

        // Combine lighting
        col = baseColor * (0.2 + 0.8 * diff) + spec * vec3(1.0);
        col *= ao;

        // Add depth-based glow
        float glow = smoothstep(0.0, 15.0, t);
        col += 0.3 * glowFactor * vec3(1.0, 0.8, 0.6) * (1.0 - glow);
    }

    // Gamma correction
    col = pow(col, vec3(0.4545));


    gl_FragColor = vec4(col, 1.0);
}

