//MW :)
uniform vec2 u_resolution;
uniform float u_time;
mat2 rot(float a) {
    float c = cos(a), s = sin(a);
    return mat2(c, -s, s, c);
}
vec3 mod289(vec3 x) { return x - floor(x * (1.0/289.0)) * 289.0; }
vec2 mod289(vec2 x) { return x - floor(x * (1.0/289.0)) * 289.0; }
vec3 permute(vec3 x) { return mod289(((x*34.0)+1.0)*x); }
float snoise(vec2 v) {
    const vec4 C = vec4(0.211324865, 0.366025404, -0.577350269, 0.024390244);
    vec2 i = floor(v + dot(v, C.yy));
    vec2 x0 = v - i + dot(i, C.xx);
    vec2 i1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
    vec4 x12 = x0.xyxy + C.xxzz;
    x12.xy -= i1;
    i = mod289(i);
    vec3 p = permute( permute(i.y + vec3(0.0, i1.y, 1.0))
                   + i.x + vec3(0.0, i1.x, 1.0));
    vec3 m = max(0.5 - vec3(dot(x0,x0), dot(x12.xy,x12.xy), dot(x12.zw,x12.zw)), 0.0);
    m = m*m*m*m;
    vec3 x = 2.0 * fract(p * C.www) - 1.0;
    vec3 h = abs(x) - 0.5;
    vec3 ox = floor(x + 0.5);
    vec3 a0 = x - ox;
    m *= 1.792842914 - 0.853734721 * (a0*a0 + h*h);
    vec3 g;
    g.x = a0.x * x0.x + h.x * x0.y;
    g.yz = a0.yz * x12.xz + h.yz * x12.yw;
    return 130.0 * dot(m, g);
}
vec2 kaleido(vec2 uv, float slices) {
    float angle = atan(uv.y, uv.x);
    float r = length(uv);
    float sector = 6.2831853 / slices;
    angle = mod(angle + sector * 0.5, sector) - sector * 0.5;
    return vec2(cos(angle)*r, sin(angle)*r);
}
float radial(vec2 uv) {
    return 1.0 - smoothstep(0.0, 1.0, length(uv));
}
vec3 computeColor(vec2 uv, float t) {
    vec2 warp1 = uv + 0.15 * vec2(snoise(uv * 4.0 + t), snoise(uv * 4.0 - t));
    vec2 warp2 = uv + 0.10 * vec2(snoise(uv * 8.0 - t*0.5), snoise(uv * 8.0 + t*0.5));
    vec2 warp = mix(warp1, warp2, 0.5);
    float slices = mix(6.0, 12.0, 0.5 + 0.5 * sin(t * 0.2));
    vec2 k = kaleido(warp, slices);
    k *= rot(t * 0.1);
    float n = snoise(k * 6.0 + t * 0.7);
    vec3 base = vec3(
        0.5 + 0.5 * cos(2.0 + n + t * 0.3),
        0.5 + 0.5 * cos(1.0 + n + 2.0 + t * 0.5),
        0.5 + 0.5 * cos(4.0 + n + 4.0 + t * 0.7)
    );
    return base;
}
void main() {
    vec2 uv = gl_FragCoord.xy / u_resolution;
    uv = uv * 2.0 - 1.0;
    uv.x *= u_resolution.x / u_resolution.y;
    float t = u_time * 0.8;
    vec3 col = computeColor(uv, t);
    float glow = radial(uv);
    float pulse = 0.5 + 0.5 * sin(u_time * 1.5);
    col *= mix(0.8 + 0.2 * pulse, 1.3, glow);
    col = pow(col, vec3(0.8));
    gl_FragColor = vec4(col, 1.0);
}

