// 2D rotation
mat2 rot(float a) {
    float c = cos(a), s = sin(a);
    return mat2(c, -s, s, c);
}

// Simplex noise (Ashima)
vec3 mod289(vec3 x){ return x - floor(x* (1.0/289.0)) *289.0; }
vec2 mod289(vec2 x){ return x - floor(x* (1.0/289.0)) *289.0; }
vec3 permute(vec3 x){ return mod289(((x*34.0)+1.0)*x); }
float snoise(vec2 v){
    const vec4 C = vec4(0.2113249,0.3660254,-0.5773503,0.0243902);
    vec2 i = floor(v + dot(v, C.yy));
    vec2 x0 = v - i + dot(i, C.xx);
    vec2 i1 = (x0.x > x0.y)? vec2(1,0): vec2(0,1);
    vec4 x12 = x0.xyxy + C.xxzz;
    x12.xy -= i1;
    i = mod289(i);
    vec3 p = permute( permute(i.y + vec3(0.0, i1.y, 1.0))
                   + i.x + vec3(0.0, i1.x, 1.0));
    vec3 m = max(0.5 - vec3(dot(x0,x0),dot(x12.xy,x12.xy),dot(x12.zw,x12.zw)),0.0);
    m = m*m*m*m;
    vec3 x = 2.0*fract(p*C.www)-1.0;
    vec3 h = abs(x)-0.5;
    vec3 ox = floor(x+0.5);
    vec3 a0 = x-ox;
    m *= 1.7928429 - 0.8537347*(a0*a0+h*h);
    vec3 g;
    g.x = a0.x*x0.x + h.x*x0.y;
    g.yz = a0.yz*x12.xz + h.yz*x12.yw;
    return 130.0 * dot(m,g);
}

// Starfield layer
float stars(vec2 uv, float t) {
    vec2 gv = fract(uv * 50.0) - .5;
    float d = length(gv);
    float m = smoothstep(0.02, 0.0, d);
    // twinkle
    m *= 0.8 + 0.2 * sin((floor(uv.x*50.0)+floor(uv.y*50.0))*43. + t*5.);
    return m;
}

// Petal layer: polar sine waves
float petals(vec2 uv, float t) {
    float r = length(uv);
    float a = atan(uv.y, uv.x);
    float w = sin(a * 12.0 + t * 0.8) * 0.3;
    return smoothstep(w, w + 0.02, r);
}

// Lens streaks
float streaks(vec2 uv, float t) {
    vec2 p = uv;
    float d = abs(sin(atan(p.y,p.x)*10.0 + t*2.0)) * (1.0 - length(p));
    return smoothstep(0.1, 0.0, d);
}

// Kaleido transform
vec2 kaleido(vec2 uv, float slices) {
    float ang = atan(uv.y, uv.x);
    float r = length(uv);
    float sec = 6.2831853 / slices;
    ang = mod(ang + sec*0.5, sec) - sec*0.5;
    return vec2(cos(ang)*r, sin(ang)*r);
}

// Main fragment
void main() {
    vec2 uv = (gl_FragCoord.xy / u_resolution.xy) * 2.0 - 1.0;
    uv.x *= u_resolution.x/u_resolution.y;
    float t = u_time * 0.7;

