    col = mix(col*0.6, col*1.8, g);
    gl_FragColor = vec4(col,1.0);
}

precision highp float;
uniform float u_time;
uniform vec2 u_resolution;
uniform vec2 u_mouse;
#define MAX_STEPS 100
#define MAX_DIST 100.0
#define SURF_DIST 0.001
#define PI 3.14159265
mat3 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, 0, s),
        vec3(0, 1, 0),
        vec3(-s, 0, c)
    );
}
mat3 rotateX(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(1, 0, 0),
        vec3(0, c, -s),
        vec3(0, s, c)
    );
}
float sdSphere(vec3 p, float r) {
    return length(p) - r;
}
float sdBox(vec3 p, vec3 b) {
    vec3 d = abs(p) - b;
    return length(max(d, 0.0)) + min(max(d.x, max(d.y, d.z)), 0.0);
}
float sdTorus(vec3 p, vec2 t) {
    vec2 q = vec2(length(p.xz) - t.x, p.y);
    return length(q) - t.y;
}
float smin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5 * (b - a) / k, 0.0, 1.0);
    return mix(b, a, h) - k * h * (1.0 - h);
}
float getSceneDistance(vec3 p) {
    vec3 pMod = p;
    pMod = rotateY(u_time * 0.2) * rotateX(sin(u_time * 0.1) * 0.2) * pMod;
    float t = u_time * 0.5;
    vec3 spherePos = pMod + vec3(sin(t * 1.1) * 0.5, cos(t * 0.7) * 0.5, sin(t * 0.8) * 0.5);
    float morphFactor = sin(t) * 0.5 + 0.5;
    float sphere = sdSphere(spherePos, 1.0 + sin(pMod.x * 3.0 + t) * 0.1 + sin(pMod.y * 4.0 - t) * 0.1);
    vec3 torusPos = pMod;
    torusPos = rotateX(t) * rotateY(t * 0.7) * torusPos;
    float torus = sdTorus(torusPos, vec2(1.5, 0.3 + sin(t * 2.0) * 0.1));
    vec3 modPos1 = pMod + vec3(2.0 * sin(t), 2.0 * cos(t * 1.3), 0.0);
    float smallSphere1 = sdSphere(modPos1, 0.5 + sin(t * 3.0) * 0.1);
    vec3 modPos2 = pMod + vec3(0.0, 2.0 * sin(t * 0.7), 2.0 * cos(t));
    float smallSphere2 = sdSphere(modPos2, 0.4 + cos(t * 2.5) * 0.1);
    float d = smin(sphere, torus, 0.3);
    d = smin(d, smallSphere1, 0.5);
    d = smin(d, smallSphere2, 0.4);
    float ground = p.y + 2.0;
    return min(d, ground);
}
vec3 getNormal(vec3 p) {
    float d = getSceneDistance(p);
    vec2 e = vec2(0.001, 0.0);
    vec3 n = d - vec3(
        getSceneDistance(p - e.xyy),
        getSceneDistance(p - e.yxy),
        getSceneDistance(p - e.yyx)
    );
    return normalize(n);
}
float rayMarch(vec3 ro, vec3 rd) {
    float dO = 0.0;
    for(int i = 0; i < MAX_STEPS; i++) {
        vec3 p = ro + rd * dO;
        float dS = getSceneDistance(p);
        dO += dS;
        if(dO > MAX_DIST || dS < SURF_DIST) break;
    }
    return dO;
}
float softShadow(vec3 ro, vec3 rd, float mint, float maxt, float k) {
    float res = 1.0;
    float t = mint;
    for(int i = 0; i < 16; i++) {
        if(t < maxt) {
            float h = getSceneDistance(ro + rd * t);
            if(h < 0.001) return 0.0;
            res = min(res, k * h / t);
            t += h;
        }
    }
    return res;
}
float calcAO(vec3 pos, vec3 nor) {
    float occ = 0.0;
    float sca = 1.0;
    for(int i = 0; i < 5; i++) {
        float h = 0.01 + 0.12 * float(i) / 4.0;
        float d = getSceneDistance(pos + h * nor);
        occ += (h - d) * sca;
        sca *= 0.95;
    }
    return clamp(1.0 - 3.0 * occ, 0.0, 1.0);
}
vec3 getBackground(vec3 rd) {
    float y = rd.y * 0.5 + 0.5;
    vec3 topColor = vec3(0.1, 0.12, 0.2);
    vec3 bottomColor = vec3(0.05, 0.06, 0.1);
    vec3 bgColor = mix(bottomColor, topColor, y);
    vec3 stars = vec3(0.0);
    for(int i = 0; i < 3; i++) {
        float t = u_time * (0.1 + float(i) * 0.05);
        vec3 p = vec3(rd.x * 13.0 + t, rd.y * 17.0 - t, rd.z * 11.0);
        p = fract(p) - 0.5;
        float stars1 = 1.0 / (40.0 * length(p));
        stars += vec3(stars1) * (0.5 + 0.5 * sin(vec3(0.2, 0.5, 0.9) * u_time * 0.3));
    }
    return bgColor + stars * (rd.y * 0.5 + 0.5);
}
vec3 getLight(vec3 p, vec3 rd) {
    vec3 lightPos = vec3(2.0, 5.0, 3.0);
    vec3 lightColor = vec3(1.0, 0.9, 0.8);
    vec3 secondLightPos = vec3(-3.0, 4.0, -2.0);
    vec3 secondLightColor = vec3(0.3, 0.5, 0.9);
    vec3 n = getNormal(p);
    vec3 baseColor = vec3(0.5) + 0.5 * cos(vec3(0.0, 0.33, 0.67) * PI * 2.0 + length(p) * 0.7 + u_time * 0.3);
    float metallic = 0.5 + 0.5 * sin(p.x * 2.0 + p.y * 3.0 + p.z * 1.0 + u_time * 0.2);
    float roughness = 0.1 + 0.3 * cos(p.y * 5.0 + u_time * 0.1);
    vec3 l1 = normalize(lightPos - p);
    float dif1 = clamp(dot(n, l1), 0.0, 1.0);
    float dist1 = length(lightPos - p);
    float atten1 = 1.0 / (1.0 + dist1 * dist1 * 0.05);
    vec3 l2 = normalize(secondLightPos - p);
    float dif2 = clamp(dot(n, l2), 0.0, 1.0);
    float dist2 = length(secondLightPos - p);
    float atten2 = 1.0 / (1.0 + dist2 * dist2 * 0.1);
    float shadow1 = softShadow(p, l1, 0.1, 10.0, 8.0);
    float shadow2 = softShadow(p, l2, 0.1, 5.0, 8.0);
    float ao = calcAO(p, n);
    vec3 reflected = reflect(rd, n);
    vec3 reflColor = getBackground(reflected);
    vec3 h1 = normalize(l1 - rd);
    float spec1 = pow(clamp(dot(n, h1), 0.0, 1.0), 16.0);
    vec3 h2 = normalize(l2 - rd);
    float spec2 = pow(clamp(dot(n, h2), 0.0, 1.0), 8.0);
    vec3 ambient = vec3(0.1, 0.11, 0.13) * ao;
    vec3 diffuse1 = baseColor * lightColor * dif1 * atten1 * shadow1;
    vec3 diffuse2 = baseColor * secondLightColor * dif2 * atten2 * shadow2;
    vec3 specular1 = lightColor * spec1 * (1.0 - roughness) * shadow1;
    vec3 specular2 = secondLightColor * spec2 * (1.0 - roughness) * shadow2;
    vec3 reflection = reflColor * metallic;
    return ambient + diffuse1 + diffuse2 + specular1 + specular2 + reflection;
}
void main() {
    vec2 uv = (gl_FragCoord.xy - 0.5 * u_resolution) / min(u_resolution.x, u_resolution.y);
    float camTime = u_time * 0.3;
    float camDist = 6.0;
    vec3 ro = vec3(camDist * sin(camTime), 2.0 + sin(camTime * 0.5), camDist * cos(camTime));
    vec3 lookAt = vec3(0.0, 0.0, 0.0);
    vec3 ww = normalize(lookAt - ro);
    vec3 uu = normalize(cross(ww, vec3(0.0, 1.0, 0.0)));
    vec3 vv = normalize(cross(uu, ww));
    float fov = 0.7;
    vec3 rd = normalize(uv.x * uu + uv.y * vv + fov * ww);
    float d = rayMarch(ro, rd);
    vec3 col;
    if(d < MAX_DIST) {
        vec3 p = ro + rd * d;
        col = getLight(p, rd);
        float fogFactor = 1.0 - exp(-d * 0.05);
        vec3 fogColor = getBackground(rd);
        col = mix(col, fogColor, fogFactor);
    } else {
        col = getBackground(rd);
    }
    col = col / (1.0 + col);
    col = pow(col, vec3(0.4545));
