#define MAX_DIST 120.0
#define SURF_DIST 0.001
#define PI 3.14159265359
mat3 rotateX(float theta){
    float c = cos(theta), s = sin(theta);
    return mat3(1.,0.,0.,
                0.,c,-s,
                0.,s,c);
}
float sdBox(vec3 p, vec3 b){
    vec3 d = abs(p) - b;
    return length(max(d,0.))+min(max(d.x,max(d.y,d.z)),0.);
}
float sdPlane(vec3 p){
    return p.y + 2.0;
}
float smin(float a, float b, float k){
    float h = clamp(0.5+0.5*(b-a)/k,0.,1.);
    return mix(b,a,h)-k*h*(1.-h);
}
float sceneSDF(vec3 p){
    float d = sdPlane(p);
    for(int x=-2;x<=2;x++){
      for(int z=-2;z<=2;z++){
        vec3 q = p - vec3(float(x)*6.,0.,float(z)*6.);
        float base = sdBox(q - vec3(0.,0.5,0.), vec3(1.2,0.5,1.2));
        float top = sdBox(q - vec3(0.,2.5+sin(u_time+float(x+z))*0.5,0.), vec3(1.0,0.2,1.0));
        float pillar = min(base, top);
        d = min(d, pillar);
      }
    }
    vec3 rp = rotateX(u_time*0.3)*(p - vec3(5.,-1.,5.));
    float rock = sdBox(rp, vec3(2.,1.,2.)) - 0.3;
    d = smin(d, rock, 0.5);

    return d;
}
vec3 getMaterial(vec3 p, vec3 n){
    vec3 st = vec3(0.4,0.35,0.3);
    float ao = clamp(dot(n, vec3(0.,1.,0.))*1.2, 0.,1.);
    vec3 base = mix(st*0.7, st, ao);
    float moss = smoothstep(1.0,1.5,p.y + 1.5);
    vec3 mg = vec3(0.2,0.5,0.1)*moss;
    return base + mg;
}
float raymarch(vec3 ro, vec3 rd){
    float t=0.;
    for(int i=0;i<MAX_STEPS;i++){
        float d = sceneSDF(ro + rd*t);
        t += d;
        if(d < SURF_DIST || t > MAX_DIST) break;
    }
    return t;
}
vec3 getNormal(vec3 p){
    float e=0.001;
    return normalize(vec3(
        sceneSDF(p+vec3(e,0,0)) - sceneSDF(p-vec3(e,0,0)),
        sceneSDF(p+vec3(0,e,0)) - sceneSDF(p-vec3(0,e,0)),
        sceneSDF(p+vec3(0,0,e)) - sceneSDF(p-vec3(0,0,e))
    ));
}
float getShadow(vec3 p, vec3 ldir){
    float res=1., t=0.01;
    for(int i=0;i<16;i++){
        float h = sceneSDF(p + ldir*t);
        if(h<0.001) return 0.;
        res = min(res,16.0*h/t);
        t += h;
        if(t>8.) break;
    }
    return res;
}
vec3 applyFog(vec3 c,float d){
    float f = 1. - exp(-d*0.03);
    return mix(c, vec3(0.6,0.7,0.6), f);
}
void main(){
    vec2 uv = (gl_FragCoord.xy/u_resolution)*2.-1.;
