    vec3 up = cross(fw,rt);
    vec3 rd = normalize(fw + uv.x*rt*tan(PI/4.) + uv.y*up*tan(PI/4.));
    float t = raymarch(ro,rd);
    vec3 col = vec3(0.8,0.9,1.0)*pow(max(rd.y,0.),2.);
