    float rPow = pow(r, power);
    float newTheta = theta * power;
    float newPhi = phi * power;
    float newChi = chi * power;
    return vec4(
        rPow * cos(newTheta) * cos(newPhi) * cos(newChi),
        rPow * sin(newTheta) * cos(newPhi) * cos(newChi),
        rPow * sin(newPhi) * cos(newChi),
        rPow * sin(newChi)
