    vec3 color;
    if (r < 0.7) {
        color = vec3(0.9); // White
    } else if (r < 0.8) {
        color = vec3(0.9, 0.1, 0.1); // Red
    } else if (r < 0.9) {
        color = vec3(0.1, 0.1, 0.9); // Blue
    } else {
