    vec2 gridSt = fract(st * scale);
    float line = smoothstep(0.0, 0.05, gridSt.x) * smoothstep(0.0, 0.05, gridSt.y) * 
                 smoothstep(0.0, 0.05, 1.0 - gridSt.x) * smoothstep(0.0, 0.05, 1.0 - gridSt.y);

    color = mix(vec3(0.0), color, line);

    gl_FragColor = vec4(color, 1.0);
}

