            if (rnd > 0.96) { 
                stars += pow(fract(sin(dot(starPos, vec3(12.34, 56.7, 89.1) + float(i))) * 4378.5453), 
                             15.0 + float(i) * 5.0);
            }
            starPos += rd * 0.5;
        }
        vec2 nebulaUV = rd.xy * 2.0;
        float nebula = 0.0;
        for (int i = 1; i <= 3; i++) {
            float scale = float(i) * 0.5;
            nebula += sin(nebulaUV.x * 5.0 * scale + time * 0.1) * 
                      sin(nebulaUV.y * 3.0 * scale - time * 0.2) * 
                      (0.2 / float(i));
        }
        nebula = max(0.0, nebula);
        vec3 nebulaColor = vec3(0.1, 0.03, 0.2) + 0.2 * sin(vec3(0.0, 0.33, 0.67) * 6.28 + time * 0.1);
        col += stars * vec3(0.1, 0.9, 1.0);
        col += nebula * nebulaColor;
    }
    float vlStep = 0.1;
    float vlDist = 0.0;
    vec3 vlColor = vec3(0.0);
    for (int i = 0; i < 8; i++) {
        vlDist += vlStep;
        vec3 vlPos = ro + rd * vlDist;
        float density = map(vlPos, time);
        if (density < 0.1) {
            vec3 lightDir = normalize(vec3(sin(time), 0.6, cos(time)));
            float light = max(0.0, -density * 1.0);
            vec3 lightColor = vec3(1.0, 0.1, 0.1) * light;
            vlColor += lightColor * (1.0 / (1.0 + vlDist * vlDist * 0.1));
        }
    }
    col += vlColor * 0.3;
    float vignette = 1.0 - smoothstep(0.4, 1.4, length(uv));
    col *= vignette;
    if (length(uv) > 0.5) {
        float ca = length(uv) - 0.5;
        vec3 colShift;
        colShift.r = map(ro + normalize(rd + vec3(0.01, 0.0, 0.0) * ca) * t, time);
        colShift.g = map(ro + rd * t, time);
        colShift.b = map(ro + normalize(rd + vec3(-0.01, 0.0, 0.0) * ca) * t, time);
        col = mix(col, mix(col, vec3(colShift), 0.5), ca * 2.0);
    }
    col = pow(col, vec3(0.5));
    col = col * 3.0 / (1.0 + col);
    col = 1.1 * col / (1.0 + col);
    col *= 0.8 + 0.2 * sin(time * 2.0);
    col.r *= 0.8 + 0.2 * sin(time * 1.1);
    col.g *= 0.8 + 0.2 * sin(time * 1.2 + 2.0);
    col.b *= 0.8 + 0.2 * sin(time * 1.3 + 4.0);
    float grain = fract(sin(dot(gl_FragCoord.xy, vec2(1.1, 1.2))) * 43758.5453 + time);
    col += (grain - 0.5) * 0.1;
    gl_FragColor = vec4(col, 1.0);
}

