frag:
// Enhanced Fractal Fragment Shader with Domain Warping and Volumetric Effects
precision highp float;

// Uniforms
uniform float uTime;
uniform vec2 uResolution;
uniform vec3 uColor;
uniform float uIntensity; 
uniform sampler2D uFeedbackTexture; // For temporal feedback

// Constants
const float TAU = 6.28318530718;
const float PHI = 1.61803398875;
const float MAX_DIST = 100.0;
const int MAX_ITERATIONS = 5;
const int MAX_FOLDS = 8;
const int VOLUMETRIC_STEPS = 8;

// Fast hash function for noise
float hash21(vec2 p) {
    p = fract(p * vec2(123.45, 678.91));
    p += dot(p, p + 45.32);
    return fract(p.x * p.y);
}

// Improved simplex-style noise with better gradient distribution
float noise(vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);

    // Smoother interpolation
    vec2 u = f * f * (3.0 - 2.0 * f);

    // Four corners
    float a = hash21(i);
    float b = hash21(i + vec2(1.0, 0.0));
    float c = hash21(i + vec2(0.0, 1.0));
    float d = hash21(i + vec2(1.0, 1.0));

    // Mix corners with smooth interpolation
    return mix(mix(a, b, u.x), mix(c, d, u.x), u.y);
}

// Fractal Brownian Motion for richer noise
float fbm(vec2 p) {
    float value = 0.0;
    float amplitude = 0.5;
    float frequency = 1.0;

    // Stack several noise layers
    for (int i = 0; i < 5; i++) {
        value += amplitude * noise(p * frequency);
        frequency *= 2.17;
        amplitude *= 0.5;

        // Domain warping - make noise flow
        p = vec2(p.y + value * 0.5, p.x - value * 0.5);
    }

    return value;
}

// Enhanced palette function with tonal control
vec3 palette(float t, vec3 tint) {
    // Dynamic base values affected by time
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.3, 0.4, 0.5) + vec3(sin(uTime * 0.14) * 0.1, cos(uTime * 0.11) * 0.1, sin(uTime * 0.17) * 0.1);

    // Apply tint to shift the color space
    vec3 color = a + b * cos(TAU * (c * t + d));

    // Contrast and tint adjustment
    color = mix(color, tint, 0.2 + 0.1 * sin(uTime * 0.3));
    color = pow(color, vec3(0.95, 1.0, 1.05)); // Subtle cross-processing

    return color;
}

// Optimized rotation with precomputed sin/cos
mat2 rotate2D(float angle) {
    float s = sin(angle);
    float c = cos(angle);
    return mat2(c, -s, s, c);
}

// Signed distance function for boxes with rounded corners
float sdRoundBox(vec2 p, vec2 b, float r) {
    vec2 d = abs(p) - b + r;
    return length(max(d, 0.0)) + min(max(d.x, d.y), 0.0) - r;
}

// Smooth minimum for organic shape blending
float smin(float a, float b, float k) {
    float h = max(k - abs(a - b), 0.0) / k;
    return min(a, b) - h * h * h * k * 0.16666667;
}

// Smooth maximum for interesting hollow shapes
float smax(float a, float b, float k) {
    float h = max(k - abs(a - b), 0.0) / k;
    return max(a, b) + h * h * h * k * 0.16666667;
}

// Distortion field for domain warping
vec2 distort(vec2 p, float t) {
    // Multi-layered domain warping
    vec2 offset = vec2(
        fbm(p * 0.5 + vec2(t * 0.23, -t * 0.17)),
        fbm(p * 0.5 + vec2(-t * 0.19, t * 0.15))
    ) * 2.0 - 1.0;

    return p + offset * (0.2 + 0.1 * sin(t * 0.5));
}

// Volumetric ray marching through the fractal
vec3 volumetricLighting(vec2 uv, float t, vec3 baseColor) {
    vec3 volumeColor = vec3(0.0);
    float totalDensity = 0.0;

    // Ray properties
    vec3 rayOrigin = vec3(uv * 0.5, -2.0);
    vec3 rayDir = normalize(vec3(uv * 0.3, 1.0));
    float rayLength = 3.0;

    // March through volume
    for (int i = 0; i < VOLUMETRIC_STEPS; i++) {
        // Sample position along ray
        float sampleDist = float(i) / float(VOLUMETRIC_STEPS - 1) * rayLength;
        vec3 samplePos = rayOrigin + rayDir * sampleDist;

        // Sample the distorted noise field
        vec2 warpedUV = distort(samplePos.xy + vec2(sin(t*0.2), cos(t*0.17)) * 0.2, t * 0.1);
        float density = fbm(warpedUV * (1.0 + samplePos.z * 0.2) + t * 0.04);

        // Apply density falloff with distance
        density *= 1.0 - sampleDist / rayLength;

        // Light scattering and absorption
        float lightIntensity = 0.7 + 0.3 * sin(t * 0.3 + samplePos.z * 2.0);
        lightIntensity *= exp(-sampleDist * 0.3);

        // Accumulate color
        vec3 lightColor = palette(density * 0.3 + t * 0.1 + samplePos.z * 0.2, baseColor);
        volumeColor += lightColor * density * lightIntensity * (1.0 - totalDensity);

        // Accumulate density (for transparency)
        totalDensity += density * 0.2;
        if (totalDensity > 0.95) break; // Early termination
    }

    return volumeColor * 1.5;
}

void main() {
    // Normalized pixel coordinates with dynamic aspect ratio handling
    vec2 uv = (gl_FragCoord.xy * 2.0 - uResolution.xy) / min(uResolution.x, uResolution.y);

    // Time variables
    float t = uTime * 0.15;
    float pulseFactor = 1.0 + 0.15 * sin(t * 3.0);

    // Mouse/interaction simulation
    vec2 mouseOffset = vec2(sin(t * 0.3), cos(t * 0.27)) * 0.1;

    // Read feedback texture for temporal effects
    vec2 feedbackUV = gl_FragCoord.xy / uResolution.xy;
    vec3 feedback = texture2D(uFeedbackTexture, feedbackUV).rgb;

    // Initialize color
    vec3 finalColor = vec3(0.0);

    // Dynamic iteration count based on resolution for performance
    float maxIterations = 5.0;
    if (min(uResolution.x, uResolution.y) < 800.0) maxIterations = 4.0;

    // Dynamic shape parameters
    float shapeMorph = sin(t * 0.4) * 0.5 + 0.5;

    // Main fractal generation
    for (int i = 0; i < MAX_ITERATIONS; i++) {
        if (float(i) >= maxIterations) break;

        // Apply domain warping to base coordinates
        vec2 p = distort(uv + mouseOffset, t * 0.1 + float(i) * 0.05);

        // Scale factor with dynamic oscillation
        float scale = pow(PHI, float(i)) * (1.0 + 0.15 * sin(t + float(i)));
        p = rotate2D(t * (0.1 + float(i) * 0.05) + sin(t * 0.2 + float(i)) * 0.3) * p;
        p *= scale;

        // Dynamic fold count
        float foldCount = min(float(MAX_FOLDS), 9.0 - float(i));

        // Iterated function system
        for (int j = 0; j < MAX_FOLDS; j++) {
            if (float(j) >= foldCount) break;

            // Enhanced folding algorithm
            float dotpp = dot(p, p) + 0.002;
            p = abs(p) / dotpp - (0.7 + 0.2 * sin(t * 0.3 + float(j) * 0.5));

            // Add orbital traps for interesting patterns
            float trap = length(p - vec2(sin(t * 0.5), cos(t * 0.7)) * 0.5);
            p = mix(p, p / trap, 0.05); // Subtle orbital trap effect

            // Rotate between folds
            p = rotate2D(t * 0.1 + sin(float(i) + float(j) * 0.7) * 0.3) * p;
        }

        // Dynamic shape blending using signed distance functions
        float boxDist = sdRoundBox(p, vec2(0.5 + 0.2 * sin(t + float(i))), 0.1);
        float circleDist = length(p) - (0.3 + 0.1 * sin(t * 0.7 + float(i)));
        float shape = mix(
            smin(boxDist, circleDist, 0.3), 
            smax(boxDist, -circleDist, 0.3), 
            shapeMorph
        );

        // Dynamic coloring based on distance
        vec3 col = palette(shape * 0.1 + t + float(i) * 0.4, uColor);

        // Improved glow effect with dynamic intensity
        float glow = 1.0 / (1.0 + abs(shape) * (1.8 + sin(t) * 0.5));
        col *= glow * (1.3 + 0.4 * sin(t * 2.0 + float(i)));

        // Layer blending with transparency
        float alpha = 1.0 / (float(i) + 1.1);
        finalColor += col * alpha;
    }

    // Add volumetric lighting effects
    vec3 volumeColor = volumetricLighting(uv, t, uColor);
    finalColor = mix(finalColor, volumeColor, 0.4 + 0.1 * sin(t * 0.27));

    // Temporal feedback for persistence effect
    finalColor = mix(finalColor, feedback * 0.97, 0.2 + 0.1 * sin(t * 0.45));

    // Enhanced color grading and effects
    finalColor *= 1.3 + 0.2 * sin(t * 2.1);
    finalColor = pow(finalColor, vec3(0.85, 0.9, 0.95)); // Gamma adjustment
    finalColor *= pulseFactor;

    // Improved vignette with chromatic offset
    float vignette = 1.0 - dot(uv * 0.4, uv * 0.4);
    vignette = pow(vignette, 1.7);
    finalColor *= vignette;

    // Chromatic aberration at edges
    float chromaAmount = 0.003 + 0.002 * sin(t * 0.5);
    vec2 uvR = uv * (1.0 + chromaAmount);
    vec2 uvB = uv * (1.0 - chromaAmount);
    float vignetteR = pow(1.0 - dot(uvR * 0.4, uvR * 0.4), 1.7);
    float vignetteB = pow(1.0 - dot(uvB * 0.4, uvB * 0.4), 1.7);
    finalColor.r *= vignetteR / vignette * 1.05;
    finalColor.b *= vignetteB / vignette * 1.05;

    // Apply user color with intensity control
    finalColor *= uColor * (uIntensity > 0.0 ? uIntensity : 1.0);

    // Add film grain
    float grain = hash21(uv + t) * 2.0 - 1.0;
    finalColor += grain * 0.015;

    // HDR tone mapping
    finalColor = finalColor / (1.0 + finalColor);

    // Output final color
    gl_FragColor = vec4(finalColor, 1.0);
}

vert:
attribute vec2 a_position;
void main() {
  gl_Position = vec4(a_position, 0., 1.);
