frag:
#ifdef GL_ES
precision mediump float;
#endif
uniform vec2 u_resolution;
uniform float u_time;
float hash(vec2 p) {
    p = fract(p * vec2(127.1,311.7));
    return fract(sin(dot(p,vec2(12.9898,78.233))) * 43758.5453);
}
float noise(vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);
    vec2 u = f*f*(3.0-2.0*f);
    return mix(
        mix(hash(i+vec2(0,0)), hash(i+vec2(1,0)), u.x),
        mix(hash(i+vec2(0,1)), hash(i+vec2(1,1)), u.x),
        u.y
    );
}
float roundedBox(vec3 p, vec3 b, float r){
    vec3 q = abs(p) - b;
    return length(max(q,0.0)) - r;
}
float mapCity(vec3 p, out vec2 cellID, out float h){
    cellID = floor(p.xz / 8.0);
    vec2 local = mod(p.xz,10.0) - 4.0;
    h = 4.0 + hash(cellID) * 18.0;
    float taper = 1.0 - smoothstep(h - 1.0, h, p.y);
    return roundedBox(
        vec3(local.x, p.y - h * 0.2, local.y),
        vec3(2.9, h * 0.5, 2.5) * taper,
        0.3
    );
}
float raymarch(vec3 ro, vec3 rd, out vec3 hitPos, out vec2 cell, out float height){
    float t = 5.0;
    for(int i = 0; i < 100; i++){
        vec3 p = ro + rd * t;
        float d = mapCity(p, cell, height);
        if(d < 0.001 || t > 120.0) break;
        t += d * 0.6;
    }
    hitPos = ro + rd * t;
    return t;
}
vec3 getNormal(vec3 p){
    float e = 0.01;
    vec2 tmpCell;
    float tmpH;
    float d0 = mapCity(p, tmpCell, tmpH);
    vec3 dx = vec3(e,0.,0.), dy = vec3(0.,e,0.), dz = vec3(0.,0.,e);
    float dxp = mapCity(p + dx, tmpCell, tmpH);
    float dyp = mapCity(p + dy, tmpCell, tmpH);
    float dzp = mapCity(p + dz, tmpCell, tmpH);
    return normalize(vec3(dxp - d0, dyp - d0, dzp - d0));
}
vec3 neonWindows(vec3 p, vec2 cell){
    float stripes = smoothstep(0.45, 0.5, abs(mod(p.x * 2.0, 2.0) - 1.0));
    float flick = step(0.7, noise(cell + floor(p.y * 5.0) + u_time * 0.3));
    return mix(vec3(0.02), vec3(1.0,0.1,0.8), stripes * flick);
}
vec3 volumetricFog(vec3 ro, vec3 rd){
    vec3 sumCol = vec3(0.0);
    float fogStep = 0.5;
    const int STEPS = 60;
    for(int i = 0; i < STEPS; i++){
        float t = 5.0 + fogStep * float(i);
        if(t > 60.0) break;
        vec3 pos = ro + rd * t;
        vec2 tmpCell; float tmpH;
        float d = mapCity(pos, tmpCell, tmpH);
        float occ = smoothstep(4.0, -2.0, d);
        sumCol += occ * exp(-t * 0.05) * 0.02;
    }
    return vec3(sumCol.r, sumCol.g * 1.5, sumCol.b * 2.5);
}
void main(){
    vec2 uv = (gl_FragCoord.xy / u_resolution) * 2.0 - 1.0;
    uv.x *= u_resolution.x / u_resolution.y;
    vec3 ro = vec3(sin(u_time * 0.08) * 30.0,
                   18.0 + sin(u_time * 0.1) * 1.0,
                   cos(u_time * 0.08) * 30.0);
    vec3 target = vec3(0.0, 5.0, 0.0);
    vec3 fw = normalize(target - ro);
    vec3 rt = normalize(cross(vec3(0.0,1.0,0.0), fw));
    vec3 up = cross(fw, rt);
    vec3 rd = normalize(uv.x * rt + uv.y * up + fw);
    vec3 hit; vec2 cell; float h;
    float t = raymarch(ro, rd, hit, cell, h);
    vec3 col = vec3(0.0);
    if(t < 120.0){
        vec3 n = getNormal(hit);
        vec3 neon = neonWindows(hit, cell);
        vec3 light = normalize(vec3(0.5,1.0,0.3));
        float diff = clamp(dot(n, light), 0.0, 1.0);
        col = mix(vec3(0.02,0.02,0.04), neon, neon) + diff * 0.3;
    }
    col += volumetricFog(ro, rd);
    col = pow(col, vec3(0.8, 0.9, 1.0));
    gl_FragColor = vec4(col, 1.0);
}

vert:
attribute vec2 a_position;
void main() {
  gl_Position = vec4(a_position, 0., 1.);
}

frag:
#ifdef GL_ES
precision mediump float;
#endif

uniform vec2 u_resolution;
uniform float u_time;

//—————————————————————
// Hash & Noise
float hash(vec2 p) {
    p = fract(p * vec2(127.1,311.7));
    return fract(sin(dot(p,vec2(12.9898,78.233))) * 43758.5453);
}
float noise(vec2 p) {
    vec2 i = floor(p), f = fract(p);
    vec2 u = f*f*(3.0-2.0*f);
    return mix(
      mix(hash(i+vec2(0,0)), hash(i+vec2(1,0)), u.x),
      mix(hash(i+vec2(0,1)), hash(i+vec2(1,1)), u.x),
      u.y
    );
}

//—————————————————————
// SDF Primitives
float roundedBox(vec3 p, vec3 b, float r) {
    vec3 q = abs(p) - b;
    return length(max(q,0.0)) - r;
}

//—————————————————————
// City Layout
float mapCity(vec3 p, out vec2 cell, out float h) {
    cell = floor(p.xz/8.0);
    vec2 loc = mod(p.xz,8.0)-4.0;
    h = 5.0 + hash(cell)*20.0;
    float taper = 1.0 - smoothstep(h-2.0, h, p.y);
    return roundedBox(
      vec3(loc.x, p.y-h*0., loc.y),
      vec3(2.5,h*0.1,2.5)*taper,
      0.4
    );
}

//—————————————————————
// Raymarch
float raymarch(vec3 ro, vec3 rd, out vec3 pos, out vec2 cell, out float h) {
    float t=0.;
    for(int i=0;i<120;i++){
        vec3 p = ro + rd*t;
        float d = mapCity(p, cell, h);
        if(d<0.001||t>150.) break;
        t += d*0.6;
    }
    pos = ro + rd*t;
    return t;
}

//—————————————————————
// Normal
vec3 getNorm(vec3 p) {
    float e=0.01;
    vec2 c; float hh;
    float d0 = mapCity(p, c, hh);
    float dx = mapCity(p+vec3(e,0,0), c, hh)-d0;
    float dy = mapCity(p+vec3(0,e,0), c, hh)-d0;
    float dz = mapCity(p+vec3(0,0,e), c, hh)-d0;
    return normalize(vec3(dx,dy,dz));
}

//—————————————————————
// Neon Windows & Signs
vec3 neonWindows(vec3 p, vec2 c) {
    float w = smoothstep(0.4,0.5,abs(mod(p.x*3.,2.)-1.));
    float flick = step(0.6,noise(c+floor(p.y*6.)+u_time*3.));
    return mix(vec3(0.01), vec3(0.0,1.0,0.8), w*flick);
}
vec3 neonSign(vec2 uv) {
    // a simple pulsing logo
    float circle = smoothstep(0.5,0.48,length(uv));
    float pulse = 0.5+0.5*sin(u_time*4. + uv.x*3. + uv.y*5.);
    return vec3(1.0,0.2,0.9)*circle*pulse;
}
//—————————————————————
// Wet Street Reflection
vec3 reflectGround(vec3 ro, vec3 rd) {
    // find plane intersection y=0
    float t = -ro.y/rd.y;
    if(t<0.) return vec3(0);
    vec3 p = ro + rd*t;
    // reflect city SDF to sample color
    vec3 rdir = reflect(rd, vec3(0,1,0));
    vec3 hit; vec2 cell; float h;
    float tt=0.;
    for(int i=0;i<60;i++){
        vec3 q = p + rdir*tt;
        float d = mapCity(q, cell, h);
        if(d<0.001) break;
        tt += d*0.7;
    }
    vec3 refl = vec3(0);
    if(tt<150.) {
        vec3 norm = getNorm(p+rdir*tt);
        float diff = clamp(dot(norm, vec3(0.3,1.0,0.2)),0.,1.);
        refl = neonWindows(p+rdir*tt, cell)*1.2 + diff*0.3;
    }
    // water distortion
    refl = mix(refl, vec3(0.03,0.05,0.1), 0.5);
    float rip = sin(p.x*4. + u_time*6.)*sin(p.z*4. + u_time*5.)*0.02;
    return refl + rip;
}

//—————————————————————
// Main
void main(){
    vec2 uv = (gl_FragCoord.xy/u_resolution)*2.-1.;
    uv.x *= u_resolution.x/u_resolution.y;

    // camera
    vec3 ro = vec3(sin(u_time*0.06)*40.,15.,cos(u_time*0.06)*40.);
    vec3 tgt=vec3(0,6,0);
    vec3 fw=normalize(tgt-ro), rt=normalize(cross(vec3(0,1,0),fw)), up=cross(fw,rt);
    vec3 rd=normalize(uv.x*rt+uv.y*up+fw);

    // raymarch scene
    vec3 hit; vec2 cell; float h;
    float dist = raymarch(ro, rd, hit, cell, h);

    vec3 col = vec3(0.02,0.03,0.05)*exp(-dist*0.01);

    if(dist<150.) {
        vec3 n = getNorm(hit);
        vec3 win = neonWindows(hit, cell);
        float diff = clamp(dot(n, vec3(0.4,1.0,0.2)),0.,1.);
        col = mix(col, win, win) + diff*0.5;
    }

    // neon billboard
    if(hit.y>h-1.0 && hit.y<h+2.0 && length(hit.xz - vec2(0,0))<3.0) {
        vec2 bUV = hit.xz/3.0;
        col += neonSign(bUV)*smoothstep(2.5,2.0,length(hit.xz));
    }

    // wet reflection
    vec3 refl = reflectGround(ro, rd);
    col = mix(col, refl, smoothstep(0.0,-1.0,rd.y)*0.6);

    // volumetric neon fog
    col += pow(noise(rd.xz*2. - u_time*0.3),2.)*vec3(0.1,0.02,0.2);

    // bloom-like glow (simple screen-space blur)
    float glow = smoothstep(1.0,0.8,length(col-vec3(0.03)));
    col += col*glow*0.3;


    // final grade
    col = pow(col, vec3(0.7,0.85,1.0));

    gl_FragColor = vec4(col,1.0);
