frag:
#ifdef GL_ES
precision mediump float;
#endif
uniform vec2 u_resolution;
uniform float u_time;
float hash(vec2 p) {
    p = fract(p * vec2(127.1,311.7));
    return fract(sin(dot(p,vec2(12.9898,78.233))) * 43758.5453);
}
float noise(vec2 p) {
    vec2 i = floor(p), f = fract(p);
    vec2 u = f*f*(3.0-2.0*f);
    return mix(
      mix(hash(i+vec2(0,0)), hash(i+vec2(1,0)), u.x),
      mix(hash(i+vec2(0,1)), hash(i+vec2(1,1)), u.x),
      u.y
    );
}
float roundedBox(vec3 p, vec3 b, float r) {
    vec3 q = abs(p) - b;
    return length(max(q,0.0)) - r;
}
float mapCity(vec3 p, out vec2 cellID, out float height) {
    cellID = floor(p.xz / 8.0);
    vec2 local = mod(p.xz, 8.0) - 4.0;
    height = 8.0 + hash(cellID) * 24.0;
    float taper = 1.0 - smoothstep(height - 3.0, height, p.y);
    float building = roundedBox(
        vec3(local.x, p.y - height * 0.5, local.y),
        vec3(3.0, height * 0.5, 3.0) * taper,
        0.4
    );
    float bridge = 1e5;
    if (mod(cellID.x + cellID.y, 5.0) < 1.0) {
        vec3 bp = p - vec3((cellID.x + 0.5) * 8.0 - 4.0, height * 0.6, (cellID.y + 0.5) * 8.0 - 4.0);
        bridge = roundedBox(bp, vec3(8.0, 0.2, 1.0), 0.1);
    }
    return min(building, bridge);
}
float raymarch(vec3 ro, vec3 rd, out vec3 hitPos, out vec2 cellID, out float height) {
    float t = 0.0;
    for (int i = 0; i < 120; i++) {
        vec3 p = ro + rd * t;
        float d = mapCity(p, cellID, height);
        if (d < 0.001 || t > 200.0) break;
        t += d * 0.6;
    }
    hitPos = ro + rd * t;
    return t;
}
vec3 getNormal(vec3 p) {
    float e = 0.02;
    vec2 tmpCell;
    float tmpH;
    float d0 = mapCity(p, tmpCell, tmpH);
    float dx = mapCity(p + vec3(e,0,0), tmpCell, tmpH) - d0;
    float dy = mapCity(p + vec3(0,e,0), tmpCell, tmpH) - d0;
    float dz = mapCity(p + vec3(0,0,e), tmpCell, tmpH) - d0;
    return normalize(vec3(dx, dy, dz));
}
vec3 neonWindows(vec3 p, vec2 cellID) {
    float stripes = smoothstep(0.35, 0.45, abs(mod(p.x * 4.0, 2.0) - 1.0));
    float flick = step(0.65, noise(cellID + floor(p.y * 8.0) + u_time * 3.0));
    vec3 colorA = vec3(0.8, 0.2, 1.0);
    vec3 colorB = vec3(0.1, 1.0, 0.6);
    float pulse = sin(u_time * 5.0 + hash(cellID) * 6.0) * 0.5 + 0.5;
    return mix(vec3(0.01), mix(colorA, colorB, pulse), stripes * flick);
