vec3 holoSign(vec2 uv) {
    float ring = smoothstep(0.6, 0.58, length(uv)) 
               - smoothstep(0.5, 0.48, length(uv));
    float swirl = sin(uv.x * 10.0 + u_time * 6.0) * 0.1 
                + sin(uv.y * 12.0 + u_time * 5.0) * 0.1;
    return vec3(0.2, 1.0, 1.0) * ring * (1.0 + swirl);
