    vec3 rdir = reflect(rd, vec3(0,1,0));
    vec3 reflCol = vec3(0.0);
    float tt = 0.0;
    for (int i = 0; i < 80; i++) {
        vec3 q = p + rdir * tt;
        vec2 cellID; float height;
        float d = mapCity(q, cellID, height);
        if (d < 0.001) break;
        tt += d * 0.7;
