    if (tt < 200.0) {
        vec3 hit = p + rdir * tt;
        vec3 n = getNormal(hit);
        float diff = clamp(dot(n, normalize(vec3(0.4,1.0,0.2))), 0.0, 1.0);
        vec2 cellID; // reuse local
        cellID = floor(hit.xz / 8.0);
        reflCol = neonWindows(hit, cellID) * 1.2 + diff * 0.3;
    }
    float rip = sin(p.x * 3.0 + u_time * 4.0) * sin(p.z * 3.0 + u_time * 6.0) * 0.03;
    return mix(reflCol, vec3(0.02,0.03,0.05), 0.4) + rip;
}
void main() {
    vec2 uv = (gl_FragCoord.xy / u_resolution) * 2.0 - 1.0;
    uv.x *= u_resolution.x / u_resolution.y;
    vec3 ro = vec3(sin(u_time * 0.05) * 50.0, 36.0 + sin(u_time * 0.3) * 4.0, cos(u_time * 0.05) * 50.0);
    vec3 tgt = vec3(0.0, 10.0, 0.0);
    vec3 fw = normalize(tgt - ro);
    vec3 rt = normalize(cross(vec3(0,1,0), fw));
    vec3 up = cross(fw, rt);
    vec3 rd = normalize(uv.x * rt + uv.y * up + fw);
    vec3 hit;
    vec2 cellID;
    float height;
    float dist = raymarch(ro, rd, hit, cellID, height);
    vec3 col = vec3(0.02,0.025,0.06) * exp(-dist * 0.008);
    if (dist < 200.0) {
        vec3 n = getNormal(hit);
        vec3 win = neonWindows(hit, cellID);
        float diff = clamp(dot(n, normalize(vec3(0.5,1.0,0.3))), 0.0, 1.0);
        col = mix(col, win, win.r) + diff * 0.6;
    }
    if (hit.y > height * 0.6 && hit.y < height * 0.8) {
        vec2 bUV = (hit.xz - (cellID + 0.5) * 8.0) / 5.0;
        col += holoSign(bUV) * smoothstep(3.5, 2.8, length(bUV)) * 1.2;
    }
    vec3 refl = reflectGround(ro, rd);
    float groundMix = smoothstep(-0.1, -0.6, rd.y);
    col = mix(col, refl, groundMix * 0.2);
    col += pow(noise(rd.xz * 3.0 - u_time * 0.2), 3.0) * vec3(0.1,0.02,0.15);
    float glow = smoothstep(0.9, 0.7, length(col - vec3(0.02)));
    col += col * glow * 0.35;
    col = pow(col, vec3(0.75,0.85,1.0));
    gl_FragColor = vec4(col, 1.0);
}

