float noise(vec2 p) {
    vec2 i = floor(p), f = fract(p);
    vec2 u = f*f*(3.0-2.0*f);
    return mix(
        mix(hash(i+vec2(0,0)), hash(i+vec2(1,0)), u.x),
        mix(hash(i+vec2(0,1)), hash(i+vec2(1,1)), u.x),
        u.y
    );
}
float roundedBox(vec3 p, vec3 b, float r) {
    vec3 q = abs(p) - b;
    return length(max(q,0.0)) - r;
}
float mapCity(vec3 p, out vec2 cell, out float h) {
    cell = floor(p.xz / 8.0);
    vec2 loc = mod(p.xz,8.0)-4.0;
    h = 10.0 + hash(cell)*30.0;
    float taper = 1.0 - smoothstep(h-2.0, h, p.y);
    float bld = roundedBox(vec3(loc.x, p.y-h*0.5, loc.y),
                           vec3(3.0, h*0.5,3.0)*taper, 0.4);
    float edge = max(
      smoothstep(0.48,0.5,abs(loc.x)-2.5),
      smoothstep(0.48,0.5,abs(loc.y)-2.5)
    ) * sin(u_time*5.0 + cell.x*1.3 + cell.y*2.1) * 0.8;
    float bridge = 1e5;
    if(mod(cell.x+cell.y,1.0)<1.0) {
      vec3 bp = p - vec3((cell.x+0.5)*8.0-4.0, h*0.6, (cell.y+0.5)*8.0-4.0);
      bridge = roundedBox(bp, vec3(6.0,0.15,1.0), 0.1);
    }
