vec3 neonWindows(vec3 p, vec2 cell) {
    float w = smoothstep(0.4,0.5,abs(mod(p.x*5.0,2.0)-1.0));
    float f = step(0.7,noise(cell + floor(p.y*10.0) + u_time*0.2));
    vec3 A = vec3(0.2,1.0,0.8), B = vec3(1.0,0.1,0.9);
