vec3 foldedAd(vec3 p, vec2 cell, float h) {
    vec3 ad = vec3(0.0);
    if(p.y > h*0.7 && p.y < h*0.9) {
        vec2 uvf = (p.xz - (cell+0.5)*8.0 + 4.0) / 6.0;
        ad += vec3(1.0,0.3,0.6) * smoothstep(0.5,0.48, length(fract(uvf*3.0)-0.5));
