vec3 groundGrid(vec2 uv) {
    float d = length(uv);
    float rings = smoothstep(0.1,0.11,fract(d*10.0 - u_time*2.0));
    float lines = step(0.01,abs(fract(uv.x*5.0)-0.5))
                + step(0.01,abs(fract(uv.y*5.0)-0.5));
    return vec3(0.0,0.8,1.0) * (rings + lines*0.2);
