    }
    return sum;
}
void main(){
    vec2 uv = (gl_FragCoord.xy/u_resolution)*2.0 - 1.0;
    uv.x *= u_resolution.x/u_resolution.y;
    vec3 sky = mix(vec3(0.01,0.0,0.05), vec3(0.05,0.02,0.1), uv.y*0.5+0.5);
    sky += rain(gl_FragCoord.xy/u_resolution)*vec3(0.1,0.4,0.6)*0.3;
