    float d = raymarch(ro, rd, hit, cell, h);
    vec3 col = sky;
    if(d < 1000.0) {
        vec3 N = getNormal(hit);
        vec3 win = neonWindows(hit, cell);
        float diff = clamp(dot(N, normalize(vec3(0.5,1.0,0.3))), 0.0, 1.0);
        col = mix(col, win + foldedAd(hit, cell, h), win.r) + diff*0.5;
    }
    col += lightBeams(ro, rd);
    float g = smoothstep(0.9,0.7,length(col-vec3(0.02)));
    col += col * g * 0.4;
    gl_FragColor = vec4(col,1.0);
}

