frag:
#ifdef GL_ES
precision highp float;
#endif
uniform float uTime;
uniform vec2  uResolution;
uniform vec3  uColor;
uniform float uIntensity;
vec3 palette(float t) {
    const float TAU = 6.28318530718;
    vec3 a = vec3(0.5), b = vec3(0.5), c = vec3(1.0);
    vec3 d = vec3(0.263,0.416,0.557) + 0.1*sin(uTime*0.1);
    return a + b*cos(TAU*(c*t + d));
}
vec3 fold3(in vec3 p, in float scale) {
    p = mod(p + scale*0.5, scale) - scale*0.5;
    return abs(p) - scale*0.25;
}
float mandelbulbDE(vec3 p){
    vec3 z = p;
    float dr = 1.0, r = 0.0;
    const float POWER = 8.0;
    for(int i=0;i<10;i++){
        r = length(z);
        if(r>4.0) break;
        float theta = acos(z.z/r);
        float phi   = atan(z.y, z.x);
        dr = pow(r,POWER-1.0)*POWER*dr + 1.0;
        float zr = pow(r,POWER);
        theta *= POWER;
        phi   *= POWER;
        z = zr * vec3(
            sin(theta)*cos(phi),
            sin(phi)*sin(theta),
            cos(theta)
        ) + p;
    }
    return 0.5*log(r)*r/dr;
}
float sceneDE(vec3 p){
    const int FOLDS = 1;
    float baseScale = 6.0 + sin(uTime*0.3)*2.0;
    for(int i=0;i<FOLDS;i++){
        float sc = baseScale * pow(0.6, float(i));
        p = fold3(p, sc);
    }
    return mandelbulbDE(p);
}
float raymarch(vec3 ro, vec3 rd){
    float t = 0.0;
    for(int i=0;i<80;i++){
        vec3 pos = ro + rd*t;
        float d = sceneDE(pos);
        if(d < 0.001 || t > 60.0) break;
        t += d * 0.5;
    }
    return t;
}
vec3 getNormal(vec3 p){
    float e = 0.0007;
    return normalize(vec3(
        sceneDE(p+vec3(e,0,0)) - sceneDE(p-vec3(e,0,0)),
        sceneDE(p+vec3(0,e,0)) - sceneDE(p-vec3(0,e,0)),
        sceneDE(p+vec3(0,0,e)) - sceneDE(p-vec3(0,0,e))
    ));
}
vec3 shade(vec3 p, vec3 rd){
    vec3 n = getNormal(p);
    vec3 L = normalize(vec3(0.5,1.0,0.3));
    float diff = clamp(dot(n,L), 0.0, 1.0);
    float spec = pow(clamp(dot(reflect(rd,n),L),0.0,1.0), 24.0);
    vec3 base = mix(vec3(0.1,0.1,0.2), vec3(0.2,0.4,1.0), diff);
    vec3 glow = vec3(1.0,0.3,0.8)*spec;
    return base + glow;
}
void main(){
    vec2 uv = (gl_FragCoord.xy*2.0 - uResolution.xy) / min(uResolution.x,uResolution.y);
    vec3 ro = vec3(0.0, 0.0, -20.0);
    float ca = uTime*0.1;
    ro.xz = mat2(cos(ca),-sin(ca),sin(ca),cos(ca))*ro.xz;
    vec3 fw = normalize(-ro);
    vec3 rt = normalize(cross(vec3(0,1,0), fw));
    vec3 up = cross(fw, rt);
    vec3 rd = normalize(uv.x*rt + uv.y*up + fw);
    float t = raymarch(ro, rd);
    vec3 col = vec3(0.0);
    if(t<60.0){
        vec3 pos = ro + rd*t;
        col = shade(pos, rd);
        float pt = t*0.02 + uTime*0.1;
        col = mix(col, palette(pt), 0.5);
        col += 0.2 * exp(-0.1*t) * vec3(0.3,0.1,0.5);
    }
    float pulse = 1.0 + 0.15*sin(uTime*2.0);
    float vig   = pow(1.0 - dot(uv*0.6, uv*0.6), 1.5);
    float noise = fract(sin(dot(uv+uTime,vec2(12.9898,78.233)))*43758.5453)*0.02;
    col *= pulse * vig;
    col += noise;
    col *= uColor * max(uIntensity,1.0);
    col = pow(col, vec3(0.8,0.85,0.9));
    gl_FragColor = vec4(col,1.0);
}

vert:
attribute vec2 a_position;
void main() {
  gl_Position = vec4(a_position, 0., 1.);
