frag:
precision highp float;

uniform vec2 u_resolution;
uniform float u_time;

// Hash function for pseudo-random numbers
float hash(vec2 p) {
    p = fract(p * vec2(123.34, 456.21));
    p += dot(p, p + 45.32);
    return fract(p.x * p.y);
}

// Value noise function
float noise(vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);

    // Cubic Hermite interpolation
    vec2 u = f * f * (3.0 - 2.0 * f);

    // Bilinear interpolation
    return mix(
        mix(hash(i + vec2(0.0, 0.0)), hash(i + vec2(1.0, 0.0)), u.x),
        mix(hash(i + vec2(0.0, 1.0)), hash(i + vec2(1.0, 1.0)), u.x),
        u.y
    );
}

// Fractal Brownian Motion
float fbm(vec2 p) {
    float value = 0.0;
    float amplitude = 0.5;
    float frequency = 1.0;

    // Rotate to avoid axis-aligned patterns
    mat2 rot = mat2(cos(0.5), sin(0.5), -sin(0.5), cos(0.5));

    for (int i = 0; i < 6; i++) {
        value += amplitude * noise(p);
        p = rot * p * 2.0 + 0.7;
        amplitude *= 0.5;
    }

    return value;
}

// Worley noise (cellular/voronoi)
float worley(vec2 p) {
    vec2 ip = floor(p);
    vec2 fp = fract(p);

    float d = 1.0;

    // Check neighboring cells
    for (int y = -1; y <= 1; y++) {
        for (int x = -1; x <= 1; x++) {
            vec2 offset = vec2(float(x), float(y));

            // Random position of feature point in neighboring cell
            vec2 pos = offset + hash(ip + offset) * 0.5 + 0.5;

            // Distance to feature point
            float dist = length(fp - pos);

            // Keep track of minimum distance
            d = min(d, dist);
        }
    }

    return d;
}

// Create a veiny pattern based on worley noise
float veinPattern(vec2 p) {
    // Multiple layers of worley noise
    float w1 = worley(p * 5.0);
    float w2 = worley(p * 10.0 + 30.0);
    float w3 = worley(p * 20.0 - 15.0);

    // Combine layers
    float veins = smoothstep(0.0, 0.15, w1) * 
                smoothstep(0.0, 0.1, w2) * 
                smoothstep(0.0, 0.05, w3);

    // Make thinner veins
    veins = pow(veins, 0.25);

    return veins;
}

void main() {
    // Normalized coordinates
    vec2 uv = gl_FragCoord.xy / u_resolution.xy;
    uv = (uv * 2.0 - 1.0) * 1.5;
    uv.x *= u_resolution.x / u_resolution.y; // Aspect ratio correction

    // Add slow movement to the scene
    vec2 movement = vec2(
        sin(u_time * 0.2) * 0.2,
        cos(u_time * 0.3) * 0.2
    );

    // Create warped surface with fbm
    float warp = fbm(uv + movement);
    vec2 warpedUV = uv + vec2(
        sin(warp * 5.0 + u_time * 0.1) * 0.1,
        cos(warp * 6.0 - u_time * 0.2) * 0.1
    );

    // Generate vein pattern
    float veins = veinPattern(warpedUV);

    // Apply flowing animation to veins
    veins = mix(
        veins,
        veinPattern(warpedUV + vec2(0.03, 0.05) * sin(u_time * 0.5)),
        sin(u_time * 0.2) * 0.5 + 0.5
    );

    // Create pulsing effect
    float pulse = (sin(u_time * 2.0) * 0.5 + 0.5) * 0.3 + 0.7;
    veins *= pulse;

    // Create base light color with subtle variation
    vec3 baseColor = mix(
        vec3(0.0, 0.8, 0.5), // Cyan/green
        vec3(0.3, 0.1, 0.8), // Purple
        sin(warpedUV.x * 3.0 + u_time * 0.3) * 0.5 + 0.5
    );

    // Add color variation based on position
    baseColor = mix(
        baseColor,
        vec3(0.9, 0.4, 0.1), // Orange-ish
        sin(warpedUV.y * 2.0 - u_time * 0.2) * 0.5 + 0.5
    );

    // Create the vein color (brighter where veins are)
    vec3 veinColor = baseColor * (1.0 - veins) + vec3(1.0) * veins;

    // Add glow effect
    float glow = pow(1.0 - veins, 2.0) * 2.0;
    veinColor += baseColor * glow * 0.5;

    // Dark background
    vec3 backgroundColor = vec3(0.02, 0.03, 0.05);

    // Combine veins with background
    vec3 finalColor = mix(
        backgroundColor,
        veinColor,
        smoothstep(0.6, 0.9, 1.0 - veins) * 0.9
    );

    // Add subtle vignette
    float vignette = 1.0 - dot(uv * 0.5, uv * 0.5);
    finalColor *= vignette;

    // Enhance contrast
    finalColor = pow(finalColor, vec3(0.8));

    // Output final color
    gl_FragColor = vec4(finalColor, 1.0);
}

