frag:
precision highp float;
uniform vec2 u_resolution;
uniform float u_time;

// Noise functions from https://www.shadertoy.com/view/4dS3Wd
// By Morgan McGuire @morgan3d, http://graphicscodex.com

float hash(float n) { return fract(sin(n) * 1e4); }
float hash(vec2 p) { return fract(1e4 * sin(17.0 * p.x + p.y * 0.1) * (0.1 + abs(sin(p.y * 13.0 + p.x)))); }

float noise(float x) {
    float i = floor(x);
    float f = fract(x);
    float u = f * f * (3.0 - 2.0 * f);
    return mix(hash(i), hash(i + 1.0), u);
}

float noise(vec2 x) {
    vec2 i = floor(x);
    vec2 f = fract(x);
    float a = hash(i);
    float b = hash(i + vec2(1.0, 0.0));
    float c = hash(i + vec2(0.0, 1.0));
    float d = hash(i + vec2(1.0, 1.0));
    vec2 u = f * f * (3.0 - 2.0 * f);
    return mix(a, b, u.x) + (c - a) * u.y * (1.0 - u.x) + (d - b) * u.x * u.y;
}

// FBM (Fractal Brownian Motion)
float fbm(vec2 p) {
    const int octaves = 5;
    float value = 0.0;
    float amplitude = 0.5;
    float frequency = 3.0;

    for (int i = 0; i < octaves; i++) {
        value += amplitude * noise(p * frequency);
        amplitude *= 0.5;
        frequency *= 2.0;
    }
    return value;
}

// Plasma field function
vec3 plasma(vec2 uv, float time) {
    // Multiple layers of swirling noise
    float n1 = fbm(uv * 1.0 + time * 0.1);
    float n2 = fbm(uv * 2.0 - time * 0.15);
    float n3 = fbm(uv * 4.0 + time * 0.05);

    // Combine layers with different speeds and scales
    float n = n1 * 0.5 + n2 * 0.25 + n3 * 0.25;

    // Add some swirling movement
    vec2 swirl = vec2(
        sin(uv.x * 3.0 + time + n * 200.0),
        cos(uv.y * 2.0 + time * 0.7 - n * 1.5)
    );

    float energyPulse = noise(time * 0.5) * 0.4 + 0.6; // Global energy pulsation

    // Add the swirl effect to our noise
    n += fbm(uv + swirl * 0.3) * 0.3;

    // Create color palette for plasma
    vec3 color1 = vec3(0.1, 0.4, 0.9); // Blue core
    vec3 color2 = vec3(0.8, 0.2, 0.8); // Purple mid
    vec3 color3 = vec3(1.0, 0.3, 0.1); // Orange hot spots

    // Create energy core glow from center
    float dist = length(uv);
    float glow = 0.15 / (dist + 0.3) * energyPulse;

    // Mix colors based on noise value
    vec3 color = mix(color1, color2, n);
    color = mix(color, color3, pow(n, 3.0));

    // Add bright veins
    float veins = pow(n, 5.0) * 2.0;
    color += vec3(1.0, 0.6, 0.3) * veins;

    // Add central glow
    color += vec3(0.2, 0.5, 1.0) * glow;

    // Add pulsing effect
    color *= 0.8 + 0.2 * sin(time * 3.0 + n * 5.0);

    // Add some dark areas
    color *= 0.7 + 0.3 * smoothstep(0.3, 0.7, n);

    return color;
}

void main() {
    // Normalized pixel coordinates with aspect ratio correction
    vec2 uv = (gl_FragCoord.xy - 0.5 * u_resolution.xy) / min(u_resolution.x, u_resolution.y);

    // Add some slow rotation to the entire effect
    float angle = u_time * 0.1;
    mat2 rotation = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    uv = rotation * uv;

    // Calculate plasma field
    vec3 color = plasma(uv, u_time);

    // Vignette effect
    float vignette = 1.0 - dot(uv, uv) * 0.5;
    color *= vignette;

    // Gamma correction for better display
    color = pow(color, vec3(0.8));

    gl_FragColor = vec4(color, 1.0);
}

