    p = fract(p * vec2(123.34, 456.21));
    p += dot(p, p + 45.32);
    return fract(p.x * p.y);
}
vec2 voronoi(vec2 uv) {
    vec2 gv = fract(uv) - 0.5;
    vec2 id = floor(uv);
    vec2 minDist = vec2(1.0);
    for (int y = -1; y <= 1; y++) {
        for (int x = -1; x <= 1; x++) {
            vec2 offset = vec2(float(x), float(y));
            vec2 n_id = id + offset;
            float h = hash21(n_id);
            vec2 p = offset + sin(h * 6.2831 + u_time * 0.5) * 0.5;
            float d = length(gv - p);
            if (d < minDist.x) {
                minDist.y = minDist.x;
                minDist.x = d;
            } else if (d < minDist.y) {
                minDist.y = d;
            }
        }
    }
    return minDist;
}
float fbm(vec2 p) {
    float value = 0.0;
    float amplitude = 0.5;
    float frequency = 1.0;
    for (int i = 0; i < 5; i++) {
        float noise = hash21(p * frequency);
        value += amplitude * noise;
        amplitude *= 0.5;
        frequency *= 2.0;
    }
    return value;
}
float organicConnection(vec2 uv) {
    vec2 v = voronoi(uv * 12.0);
    float edge = smoothstep(0.01, 0.09, v.y - v.x);
    float branch = smoothstep(0.02, 0.04, v.x);
    float pulse = 0.5 + 0.5 * sin(u_time * 0.5);
    branch *= 0.8 + 0.2 * pulse;
    return edge * branch;
}
