    vec2 moveUV = uv + vec2(u_time * 0.1, u_time * 0.05);
    float conn1 = organicConnection(moveUV);
    float conn2 = organicConnection(moveUV * 3.0 + 3.5);
    float conn3 = organicConnection(moveUV * 0.5 - 5.0);
    vec3 color = vec3(0.02, 0.02, 0.03);
    float vein1 = pow(conn1, 0.8) * 1.5;
    float vein2 = pow(conn2, 1.2) * 1.2;
    float vein3 = pow(conn3, 0.7) * 0.8;
    color += vec3(0.0, 0.5, 0.8) * vein1;
    color += vec3(0.6, 0.1, 0.8) * vein2;
    color += vec3(0.1, 0.6, 0.5) * vein3;
    float pulse = 0.5 + 0.5 * sin(u_time);
    float pulseFast = 0.5 + 0.5 * sin(u_time * -0.1);
    float glow = max(vein1, max(vein2, vein3));
    color += vec3(0.0, 0.2, 0.4) * glow * pulse;
    float crossings = vein1 * vein2 + vein2 * vein3 + vein1 * vein3;
    color += vec3(1.0, 0.8, 1.0) * crossings * 2.0 * pulseFast;
    float ao = fbm(uv * 5.0);
    color *= 0.8 + 0.2 * ao;
