// Pseudo-random number generator
float random(vec2 st) {
    return fract(sin(dot(st.xy, vec2(12.9898, 78.233))) * 43758.5453);
}

// 2D rotation matrix
mat2 rotate2D(float angle) {
    return mat2(cos(angle), -sin(angle),
                sin(angle), cos(angle));
}

// Value noise function
float noise(vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    // Smooth interpolation
    vec2 u = f * f * (3.0 - 2.0 * f);

    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

// Fractal Brownian Motion
float fbm(vec2 st) {
    float value = 0.0;
    float amplitude = 0.5;
    float frequency = 1.0;

    // 5 octaves of noise
    for (int i = 0; i < 5; i++) {
        value += amplitude * noise(st * frequency);
        st = rotate2D(0.5) * st;
        frequency *= 2.0;
        amplitude *= 0.5;
    }

    return value;
}

// 3D effect function
float depthEffect(vec2 st, float time) {
    float depth = fbm(st * 2.0 + time * 0.1);
    depth = 0.5 + 0.5 * sin(depth * 3.14159 * 2.0 + time);
    return depth;
}

void main() {
    vec2 st = gl_FragCoord.xy / u_resolution.xy;
    vec2 mouseNorm = u_mouse / u_resolution.xy;

    // Create a dynamic scaling factor that changes over time and position
    float baseScale = 5.0;
    float dynamicScale = baseScale + 3.0 * sin(u_time * 0.2);

    // Apply spiral effect with time
    float angle = atan(st.y - 0.5, st.x - 0.5);
    float radius = length(st - 0.5) * 2.0;
    float spiral = angle + radius * 3.0 + u_time * 0.3;

    // Create row-dependent transformations
    float rowIndex = floor(st.y * dynamicScale);
    float rowFactor = rowIndex / dynamicScale;

    // Each row gets its own movement pattern
    float rowTime = u_time * (0.5 + mod(rowIndex, 3.0) * 0.2);
    float rowSpiral = spiral + rowIndex * 0.5;

    // Apply distortion based on row
    vec2 distortedSt = st;
    distortedSt.x += 0.1 * sin(rowTime + st.y * 10.0);
    distortedSt.y += 0.05 * cos(rowTime * 0.7 + st.x * 8.0);

    // Apply spiral distortion
    distortedSt = rotate2D(rowSpiral * 0.1) * (distortedSt - 0.5) + 0.5;

    // 3D depth effect
    float depth = depthEffect(distortedSt, rowTime);
    float adjustedScale = dynamicScale * (0.8 + 0.4 * depth);

    // Create interactive effect with mouse position
    float mouseInfluence = length(st - mouseNorm) < 0.2 ? 
                          0.3 * (1.0 - length(st - mouseNorm) * 5.0) : 0.0;
    adjustedScale *= (1.0 + mouseInfluence);

    // Create grid with the dynamic scale
    vec2 grid = floor(distortedSt * adjustedScale);

    // Make cells feed into adjacent rows
    float cellConnection = noise(grid * 0.5 + u_time * 0.1);
    float cellSize = 0.9 + 0.2 * sin(rowTime + cellConnection * 6.28);

    // Generate values for each cell with more variation
    float r = random(grid + floor(u_time * 0.2));
    float cellDepth = 0.3 + 0.7 * depthEffect(grid * 0.3, u_time * 0.5);

    // Enhanced color palette inspired by Mondrian but with depth variations
    vec3 color;
    if (r < 0.55) {
        // White with subtle depth shading
        color = vec3(0.9 - 0.2 * (1.0 - cellDepth));
    } else if (r < 0.7) {
        // Red with variations
        color = vec3(0.9, 0.1 + 0.2 * sin(u_time * 0.5 + grid.x), 0.1) * cellDepth;
    } else if (r < 0.85) {
        // Blue with variations
        color = vec3(0.1, 0.1 + 0.1 * cos(u_time * 0.3), 0.9 - 0.3 * (1.0 - cellDepth));
    } else {
        // Yellow with variations
        color = vec3(0.9, 0.9 - 0.2 * sin(u_time * 0.4 + grid.y), 0.1) * cellDepth;
    }

    // Add grid lines with dynamic width based on depth
    vec2 gridSt = fract(distortedSt * adjustedScale);
    float lineWidth = mix(0.03, 0.1, 1.0 - cellDepth);
    float innerCellEdge = lineWidth;
    float outerCellEdge = 1.0 - lineWidth;

    // Create variable line thickness based on depth and cell importance
    float lineThickness = lineWidth * (1.0 + 0.5 * cellConnection);

    // Create grid lines with 3D shadowing effect
    float line = smoothstep(0.0, lineThickness, gridSt.x) * 
                 smoothstep(0.0, lineThickness, gridSt.y) * 
                 smoothstep(0.0, lineThickness, 1.0 - gridSt.x) * 
                 smoothstep(0.0, lineThickness, 1.0 - gridSt.y);

    // Make borders thicker at certain intervals for more Mondrian-like composition
    if (mod(grid.x, 3.0) == 0.0 || mod(grid.y, 2.0) == 0.0) {
        line *= smoothstep(0.0, lineThickness * 2.0, gridSt.x) * 
               smoothstep(0.0, lineThickness * 2.0, gridSt.y) * 
               smoothstep(0.0, lineThickness * 2.0, 1.0 - gridSt.x) * 
               smoothstep(0.0, lineThickness * 2.0, 1.0 - gridSt.y);
    }

    // Create 3D effect on the edges using shadows
    vec3 lineColor = vec3(0.0);
    float shadowFactor = 0.3 * (1.0 - cellDepth);

    // Bottom and right shadow for 3D effect
    float bottomShadow = 1.0 - smoothstep(0.0, lineThickness * 3.0, gridSt.y);
    float rightShadow = 1.0 - smoothstep(0.0, lineThickness * 3.0, gridSt.x);
    float shadowMask = max(bottomShadow, rightShadow) * 0.5;

    // Mix color with shadow for 3D effect
    color = mix(color, color * (1.0 - shadowFactor), shadowMask * (1.0 - line));

    // Apply line masking
    color = mix(lineColor, color, line);

    // Vignette effect to enhance depth
    float vignette = 1.0 - 0.3 * length(st - 0.5);
    color *= vignette;

    // Time-based pulsing glow on some cells
    if (cellConnection > 0.7) {
        float glow = 0.2 * (1.0 + sin(u_time * 2.0 + grid.x + grid.y));
        color += glow * vec3(1.0, 0.8, 0.4) * (1.0 - line);
    }

    gl_FragColor = vec4(color, 1.0);
}

