precision highp float;
uniform float u_time;
uniform vec2 u_resolution;
#define STEPS 64
#define MAX_DIST 40.0
#define SURF_EPS 0.001
float map(vec3 p) {
    vec3 q = mod(p, 2.0) - 1.0;
    return length(max(abs(q) - vec3(0.3), 0.0)) - 0.0;
}
float raymarch(vec3 ro, vec3 rd) {
    float t = 0.0;
    for(int i=0;i<STEPS;i++){
        vec3 pos = ro + rd * t;
        float d = map(pos);
        t += d;
        if(d < SURF_EPS || t > MAX_DIST) break;
    }
    return t;
}
void main(){
    vec2 uv = (gl_FragCoord.xy - 0.5*u_resolution) / u_resolution.y;
    vec3 ro = vec3(0.0, 0.0, u_time*7.0);
    vec3 look = vec3(0.0);
    vec3 fwd = normalize(look - ro);
    vec3 right = normalize(cross(fwd, vec3(0,1,0)));
    vec3 up = cross(right, fwd);
    vec3 rd = normalize(uv.x*right + uv.y*up + 1.5*fwd);
    float t = raymarch(ro, rd);
    vec3 col;
    if(t < MAX_DIST){
        vec3 pos = ro + rd*t;
        float fog = exp(-t*0.1);
        // wall color based on angle
        float a = atan(pos.y, pos.x);
        col = vec3(0.5 + 0.5*cos(a*3.0 + u_time),
                   0.5 + 0.5*sin(a*5.0 + u_time*1.3),
                   0.5 + 0.5*cos(a*4.0 - u_time*0.7));
        col *= fog;
    } else {
        col = vec3(0.02,0.02,0.04) + 0.2*uv.xyx;
    }
    gl_FragColor = vec4(col,1.0);
}

