}
float noise(vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);
    vec2 u = f * f * f * (f * (f * 6.0 - 15.0) + 10.0);
    float a = hash21(i);
    float b = hash21(i + vec2(1.0, 0.0));
    float c = hash21(i + vec2(0.0, 1.0));
    float d = hash21(i + vec2(1.0, 1.0));
    return mix(mix(a, b, u.x), mix(c, d, u.x), u.y);
}
float fbm(vec2 p) {
    float sum = 0.0;
    float amp = 0.5;
    float freq = 1.0;
    for(int i = 0; i < 6; i++) {
        sum += noise(p * freq) * amp;
        amp *= 0.5;
        freq *= 2.0;
    }
    return sum;
}
float getBand(float index) {
    float t = u_time * BAND_ANIMATION_SPEED;
    float idx = index / float(NUM_BANDS);
    float basic = sin(t + idx * 15.0) * 0.5 + 0.5;
    float variation = fbm(vec2(idx * 3.0, t * 0.2)) * 0.5 + 0.5;
    float bassDrop = pow(sin(t * 0.25) * 0.5 + 0.5, 8.0) * sin(idx * 20.0 + t * 4.0) * 0.5;

    return mix(mix(basic, variation, 0.7), 1.0, bassDrop);
