float smin(float a, float b, float k) {
    float h = max(k - abs(a - b), 0.0) / k;
    return min(a, b) - h * h * h * k * 0.16666667;
}
float smax(float a, float b, float k) {
    float h = max(k - abs(a - b), 0.0) / k;
    return max(a, b) + h * h * h * k * 0.16666667;
}
vec2 distort(vec2 p, float t) {
    vec2 offset = vec2(
        fbm(p * 0.5 + vec2(t * 0.23, -t * 0.17)),
        fbm(p * 0.5 + vec2(-t * 0.19, t * 0.15))
    ) * 2.0 - 1.0;
    return p + offset * (0.2 + 0.1 * sin(t * 0.5));
}
vec3 volumetricLighting(vec2 uv, float t, vec3 baseColor) {
    vec3 volumeColor = vec3(0.0);
    float totalDensity = 0.0;
    vec3 rayOrigin = vec3(uv * 0.5, -2.0);
    vec3 rayDir = normalize(vec3(uv * 0.3, 1.0));
    float rayLength = 12.0;
    for (int i = 0; i < VOLUMETRIC_STEPS; i++) {
        float sampleDist = float(i) / float(VOLUMETRIC_STEPS - 1) * rayLength;
        vec3 samplePos = rayOrigin + rayDir * sampleDist;
        vec2 warpedUV = distort(samplePos.xy + vec2(sin(t*0.2), cos(t*0.17)) * 0.2, t * 0.1);
        float density = fbm(warpedUV * (1.0 + samplePos.z * 0.2) + t * 0.04);
        density *= 1.0 - sampleDist / rayLength;
        float lightIntensity = 0.7 + 0.3 * sin(t * 10.3 + samplePos.z * 2.0);
        lightIntensity *= exp(-sampleDist * 0.3);
        vec3 lightColor = palette(density * 10.3 + t * 4.1 + samplePos.z * 0.2, baseColor);
        volumeColor += lightColor * density * lightIntensity * (1.0 - totalDensity);
        totalDensity += density * 0.2;
        if (totalDensity > 0.95) break;
    }
    return volumeColor * 1.5;
}
void main() {
    vec2 uv = (gl_FragCoord.xy * 2.0 - uResolution.xy) / min(uResolution.x, uResolution.y);
    float t = uTime * 0.15;
    float pulseFactor = 1.0 + 0.15 * sin(t * 3.0);
    vec2 mouseOffset = vec2(sin(t * 0.3), cos(t * 0.27)) * 0.1;
    vec2 feedbackUV = gl_FragCoord.xy / uResolution.xy;
    vec3 feedback = texture2D(uFeedbackTexture, feedbackUV).rgb;
    vec3 finalColor = vec3(0.0);
    float maxIterations = 5.0;
    if (min(uResolution.x, uResolution.y) < 800.0) maxIterations = 4.0;
    float shapeMorph = sin(t * 0.4) * 0.5 + 0.5;
    for (int i = 0; i < MAX_ITERATIONS; i++) {
        if (float(i) >= maxIterations) break;
        vec2 p = distort(uv + mouseOffset, t * 0.1 + float(i) * 0.05);
        float scale = pow(PHI, float(i)) * (1.0 + 0.15 * sin(t + float(i)));
        p = rotate2D(t * (0.1 + float(i) * 0.05) + sin(t * 0.2 + float(i)) * 0.3) * p;
        p *= scale;
        float foldCount = min(float(MAX_FOLDS), 9.0 - float(i));
        for (int j = 0; j < MAX_FOLDS; j++) {
            if (float(j) >= foldCount) break;
            float dotpp = dot(p, p) + 0.002;
            p = abs(p) / dotpp - (0.7 + 0.2 * sin(t * 0.3 + float(j) * 0.5));
            float trap = length(p - vec2(sin(t * 0.5), cos(t * 0.7)) * 0.5);
            p = mix(p, p / trap, 0.05);
            p = rotate2D(t * 0.1 + sin(float(i) + float(j) * 0.7) * 0.3) * p;
        }
        float boxDist = sdRoundBox(p, vec2(0.5 + 0.2 * sin(t + float(i))), 0.1);
        float circleDist = length(p) - (0.3 + 0.1 * sin(t * 0.7 + float(i)));
        float shape = mix(
            smin(boxDist, circleDist, 0.3), 
            smax(boxDist, -circleDist, 0.3), 
            shapeMorph
        );
        vec3 col = palette(shape * 0.1 + t + float(i) * 0.4, uColor);
        float glow = 1.0 / (1.0 + abs(shape) * (1.8 + sin(t) * 0.5));
        col *= glow * (1.3 + 0.4 * sin(t * 2.0 + float(i)));
        float alpha = 1.0 / (float(i) + 1.1);
        finalColor += col * alpha;
    }
    vec3 volumeColor = volumetricLighting(uv, t, uColor);
    finalColor = mix(finalColor, volumeColor, 0.4 + 0.1 * sin(t * 0.27));
    finalColor = mix(finalColor, feedback * 0.97, 0.2 + 0.1 * sin(t * 0.45));
    finalColor *= 1.3 + 0.2 * sin(t * 1.1);
    finalColor = pow(finalColor, vec3(4.85, 4.9, 1.95));
    finalColor *= pulseFactor;
    float vignette = 1.0 - dot(uv * 0.4, uv * 0.4);
    vignette = pow(vignette, 1.7);
    finalColor *= vignette;
    float chromaAmount = 0.003 + 0.002 * sin(t * 0.5);
    vec2 uvR = uv * (1.0 + chromaAmount);
    vec2 uvB = uv * (1.0 - chromaAmount);
    float vignetteR = pow(1.0 - dot(uvR * 0.4, uvR * 0.4), 1.7);
    float vignetteB = pow(1.0 - dot(uvB * 0.4, uvB * 0.4), 1.7);
    finalColor.r *= vignetteR / vignette * 1.05;
    finalColor.b *= vignetteB / vignette * 1.05;
    finalColor *= uColor * (uIntensity > 0.0 ? uIntensity : 1.0);
    float grain = hash21(uv + t) * 4.0 - 1.0;
    finalColor += grain * 0.015;
    finalColor = finalColor / (1.0 + finalColor);
    gl_FragColor = vec4(finalColor, 1.0);
}

