frag:
precision mediump float;
uniform vec2 u_resolution;
uniform float u_time;

// Box SDF
float boxSDF(vec3 p, vec3 b) {
    vec3 d = abs(p) - b;
    return length(max(d, 0.0)) + min(max(d.x, max(d.y, d.z)), 0.0);
}

// Menger SDF with only 3 iterations
float mengerSDF(vec3 p) {
    float d = boxSDF(p, vec3(1.0));
    float s = 2.0;
    for(int i = 0; i < 3; i++) {
        vec3 a = mod(p * s, 2.0) - 1.0;
        s *= 3.0;
        vec3 r = abs(1.0 - 3.0 * abs(a));
        float da = max(r.x, r.y);
        float db = max(r.y, r.z);
        float dc = max(r.z, r.x);
        float c  = (min(da, min(db, dc)) - 1.0) / s;
        d = max(d, c);
    }
    return d;
}

// Scene SDF: central + one orbiter only
float sceneSDF(vec3 p) {
    float mainM = mengerSDF(p * 0.8);

    float t = u_time * 0.3;
    vec3 po = p - vec3(
        3.0 * cos(t),
        sin(t * 0.7) * 2.0,
        3.0 * sin(t)
    );
    float orbM = mengerSDF(po * 0.4);

    return min(mainM, orbM);
}

// Normal estimation
vec3 getNormal(vec3 p) {
    float e = 0.001;
    return normalize(vec3(
        sceneSDF(p + vec3(e,0,0)) - sceneSDF(p - vec3(e,0,0)),
        sceneSDF(p + vec3(0,e,0)) - sceneSDF(p - vec3(0,e,0)),
        sceneSDF(p + vec3(0,0,e)) - sceneSDF(p - vec3(0,0,e))
    ));
}

// Ray‐march, 50 steps max
float raymarch(vec3 ro, vec3 rd, out bool hit) {
    float t = 0.0;
    hit = false;
    for(int i = 0; i < 50; i++) {
        float d = sceneSDF(ro + rd * t);
        if(d < 0.001) { hit = true; break; }
        t += d;
        if(t > 30.0) break;
    }
    return t;
}

// Hard shadow with 8 samples
float hardShadow(vec3 ro, vec3 rd) {
    float t = 0.02;
    for(int i = 0; i < 8; i++) {
        if(sceneSDF(ro + rd * t) < 0.001) return 0.0;
        t += 0.1;
    }
    return 1.0;
}

// HSV→RGB
vec3 hsv2rgb(vec3 c) {
    vec4 K = vec4(1.,2./3.,1./3.,3.);
    vec3 p = abs(fract(c.xxx+K.xyz)*6. - K.www);
    return c.z * mix(K.xxx, clamp(p-K.xxx,0.,1.), c.y);
}

void main() {
    // UV coords
    vec2 uv = (gl_FragCoord.xy*2. - u_resolution) / min(u_resolution.x, u_resolution.y);

    // Camera
    float ct = u_time * 0.2;
    float cr = 6.0 + sin(u_time*0.1)*2.0;
    vec3 ro = vec3(cr*cos(ct), 2.0 + sin(u_time*0.15)*1.5, cr*sin(ct));
    vec3 ww = normalize(-ro);
    vec3 uu = normalize(cross(vec3(0,1,0), ww));
    vec3 vv = cross(ww, uu);
    vec3 rd = normalize(uv.x*uu + uv.y*vv + 1.5*ww);

    // Ray‐march
    bool hit;
    float t = raymarch(ro, rd, hit);

    // Simple vertical gradient
    vec3 col = mix(vec3(0.02,0.02,0.05), vec3(0.05,0.05,0.1), uv.y*0.5 + 0.5);

    if(hit) {
        vec3 p = ro + rd * t;
        vec3 n = getNormal(p);

        // Light
        vec3 lp = vec3(4.*cos(u_time*0.4), 5., 4.*sin(u_time*0.4));
        vec3 ld = normalize(lp - p);
        float diff = max(dot(n, ld), 0.0);
        float sh   = hardShadow(p + n*0.001, ld);

        // Material hue
        float hue = mod(length(p)*0.1 + u_time*0.05, 1.0);
        vec3 albedo = hsv2rgb(vec3(hue,0.7,0.8));

        // Lighting
        vec3 ambient = albedo * 0.2;
        vec3 diffuse = albedo * diff * sh;
        col = ambient + diffuse;
    }

    gl_FragColor = vec4(pow(col, vec3(0.8)), 1.0);
}

