frag:
#ifdef GL_ES
precision mediump float;
#endif
//julia - created by MaxWarren
uniform vec2 u_resolution;
uniform float u_time;
#define PI 3.14159265359
// HSV→RGB helper
vec3 hsb2rgb(vec3 c) {
    vec3 rgb = clamp(abs(mod(c.x*6.0 + vec3(10.0,4.5,2.0),6.0) - 3.0) - 1.0,
                     0.0, 1.0);
    rgb = rgb*rgb*(3.0 - 2.0*rgb);
    return c.z * mix(vec3(1.0), rgb, c.y);
}
// 2D Julia
float julia(vec2 z) {
    vec2 c = vec2(0.285 + 0.2*sin(u_time*0.3),
                  0.01  + 0.2*cos(u_time*0.25));
    float iter = 0.0;
    for(int i=0; i<30; i++) {
        float x = (z.x*z.x - z.y*z.y) + c.x;
        float y = (2.0*z.x*z.y)     + c.y;
        z = vec2(x,y);
        if(dot(z,z) > 4.0) { break; }
        iter += 1.0;
    }
    return iter/30.0;
}
// Tunnel rings
float map(vec3 p) {
    // twist around Z axis
    float a = u_time * 0.4;
    float ca = cos(a), sa = sin(a);
    p.xy *= mat2(ca, -sa, sa, ca);
    // fly forward
    p.z += u_time * 2.0;
    // get radius in XY
    float r = length(p.xy);
    // sample Julia at scaled coords
    float jf = julia(p.xy * 0.80);
    // make the fractal produce a fold: sharper near mid-escape
    float wave = smoothstep(0.2,0.8, jf) * 0.5;
    // target radius oscillates between 1.0 ± wave
    float target = 1.0 +  wave;
    // ring thickness
    float thickness = 0.08 + 0.04 * jf;
    return abs(r - target) - thickness;
}
// Estimate normal
vec3 getNormal(vec3 p) {
    float e = 0.001;
    return normalize(vec3(
        map(p + vec3(e,0,0)) - map(p - vec3(e,0,0)),
        map(p + vec3(0,e,0)) - map(p - vec3(0,e,0)),
        map(p + vec3(0,0,e)) - map(p - vec3(0,0,e))
    ));
}
// Ray-march
float raymarch(vec3 ro, vec3 rd) {
    float t = 0.0;
    for(int i=0; i<60; i++){
        float d = map(ro + rd*t);
        if(d<0.001) break;
        t += d;
        if(t>30.0) break;
    }
    return t;
}
void main(){
    // uv coords
    vec2 uv = (gl_FragCoord.xy*2.0 - u_resolution) / u_resolution.y;
    // camera setup
    vec3 ro = vec3(0.0, 0.0, -5.0 + sin(u_time*0.3)*1.0);
    vec3 rd = normalize(vec3(uv, 1.5));
    // march
    float t = raymarch(ro, rd);
    vec3 col = vec3(0.0);
    if(t<30.0){
        vec3 pos = ro + rd * t;
        vec3 n   = getNormal(pos);
        // simple diffuse
        vec3 light = normalize(vec3(1.0, 0.5, -1.0));
        float diff = max(dot(n, light),0.0);
        // color by fractal + depth
        float jf = julia(pos.xy * 0.8);
        float hue = fract(jf + u_time*0.1 - pos.z*0.05);
        float val = 0.5 + 0.5*diff;
        vec3 base = hsb2rgb(vec3(hue,0.9,val));
        col = base;
    }
    // vignette
    col += pow(1.0 - length(uv),3.0) * 0.2;
    // gamma
    col = pow(col,vec3(0.8));
    gl_FragColor = vec4(col,1.0);
}

