precision mediump float;
uniform float u_time;
uniform vec2 u_resolution;

void main() {
    vec2 uv = gl_FragCoord.xy / u_resolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= u_resolution.x / u_resolution.y;

    float time = u_time * 0.2;

    // Create flowing liquid effect
    vec2 distortion = vec2(
        sin(uv.y * 5.0 + time * 3.0) * 0.1,
        cos(uv.x * 4.0 + time * 2.0) * 0.1
    );

    uv += distortion;

    // Metallic surface calculation
    float surface = sin(uv.x * 8.0 + time * 4.0) * 
                   cos(uv.y * 6.0 + time * 3.0) * 0.5;

    surface += sin(uv.x * 12.0 - time * 2.0) * 
               cos(uv.y * 10.0 + time * 1.5) * 0.3;

    // Create metallic gradient
    vec3 baseColor = vec3(0.7, 0.8, 0.9);
    vec3 highlightColor = vec3(1.0, 1.0, 1.0);
    vec3 shadowColor = vec3(0.2, 0.3, 0.4);

    vec3 color = mix(shadowColor, baseColor, surface + 0.5);
    color = mix(color, highlightColor, max(0.0, surface * 2.0));

    // Add shimmer effect
    float shimmer = sin(uv.x * 20.0 + time * 8.0) * 
                   sin(uv.y * 15.0 + time * 6.0);
    color += vec3(0.1, 0.2, 0.3) * shimmer * 0.5;

    gl_FragColor = vec4(color, 1.0);
}

