precision mediump float;
uniform float u_time;
uniform vec2 u_resolution;

void main() {
    vec2 uv = gl_FragCoord.xy / u_resolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= u_resolution.x / u_resolution.y;

    float time = u_time * 0.5;

    // Convert to polar coordinates
    float radius = length(uv);
    float angle = atan(uv.y, uv.x);

    // Rotate the pattern
    angle += time;

    // Create mandala pattern
    float pattern = 0.0;

    for(int i = 1; i <= 8; i++) {
        float freq = float(i) * 2.0;
        pattern += sin(angle * freq + radius * 10.0 + time) / freq;
        pattern += cos(angle * freq * 0.5 + radius * 8.0 - time * 0.7) / (freq * 0.5);
    }

    pattern = sin(pattern + time) * 0.5 + 0.5;

    // Create radial falloff
    float radialFade = 1.0 - smoothstep(0.0, 1.2, radius);

    // Color scheme
    vec3 color1 = vec3(1.0, 0.3, 0.7);
    vec3 color2 = vec3(0.3, 0.8, 1.0);
    vec3 color3 = vec3(0.9, 0.7, 0.1);

    vec3 color = mix(color1, color2, sin(pattern * 3.14159 + time) * 0.5 + 0.5);
    color = mix(color, color3, sin(pattern * 6.28 + time * 1.3) * 0.5 + 0.5);

    color *= pattern * radialFade;

    gl_FragColor = vec4(color, 1.0);
}

