precision mediump float;
uniform float u_time;
uniform vec2 u_resolution;

float random(vec2 st) {
    return fract(sin(dot(st.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

void main() {
    vec2 uv = gl_FragCoord.xy / u_resolution.xy;

    // Create grid
    vec2 grid = vec2(20.0, 40.0);
    vec2 gridUV = fract(uv * grid);
    vec2 gridID = floor(uv * grid);

    float time = u_time * 2.0;

    // Create falling effect
    float fall = fract(time * 0.1 + random(gridID) * 10.0);

    // Character intensity based on position in column
    float charIntensity = 1.0 - abs(gridUV.y - fall);
    charIntensity = smoothstep(0.8, 1.0, charIntensity);

    // Random character change
    float charChange = step(0.9, random(gridID + floor(time * 10.0)));

    // Trail effect
    float trail = exp(-abs(gridUV.y - fall) * 8.0);

    // Color
    vec3 green = vec3(0.1, 1.0, 0.2);
    vec3 darkGreen = vec3(0.0, 0.3, 0.1);

    vec3 color = mix(darkGreen, green, charIntensity + trail * 0.3);

    // Add digital noise
    float noise = random(gridUV + time) * 0.1;
    color += noise;

    // Fade edges
    float edgeFade = smoothstep(0.0, 0.1, uv.x) * smoothstep(1.0, 0.9, uv.x);
    color *= edgeFade;

    gl_FragColor = vec4(color, 1.0);
}

