frag:
precision highp float;

uniform float u_time;
uniform vec2 u_resolution;
uniform vec2 u_mouse;

#define MAX_STEPS 100
#define MAX_DIST 100.0
#define SURF_DIST 0.001
#define PI 3.14159265

// Rotate around Y-axis
mat3 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, 0, s),
        vec3(0, 1, 0),
        vec3(-s, 0, c)
    );
}

// Rotate around X-axis
mat3 rotateX(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(1, 0, 0),
        vec3(0, c, -s),
        vec3(0, s, c)
    );
}

// Signed distance function for a sphere
float sdSphere(vec3 p, float r) {
    return length(p) - r;
}

// Signed distance function for a torus
float sdTorus(vec3 p, vec2 t) {
    vec2 q = vec2(length(p.xz) - t.x, p.y);
    return length(q) - t.y;
}

// Smooth minimum
float smin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5 * (b - a) / k, 0.0, 1.0);
    return mix(b, a, h) - k * h * (1.0 - h);
}

// Scene distance function
float getSceneDistance(vec3 p) {
    vec3 pMod = p;
    pMod = rotateY(u_time * 0.2) * rotateX(sin(u_time * 0.1) * 0.2) * pMod;

    float t = u_time * 0.5;

    // Central vortex
    vec3 vortexPos = pMod + vec3(sin(t * 1.1) * 0.5, cos(t * 0.7) * 0.5, sin(t * 0.8) * 0.5);
    float vortex = sdTorus(vortexPos, vec2(1.5, 0.3 + sin(t * 2.0) * 0.1));

    // Surrounding spheres
    vec3 spherePos1 = pMod + vec3(2.0 * sin(t), 2.0 * cos(t * 1.3), 0.0);
    float sphere1 = sdSphere(spherePos1, 0.5 + sin(t * 3.0) * 0.1);

    vec3 spherePos2 = pMod + vec3(0.0, 2.0 * sin(t * 0.7), 2.0 * cos(t));
    float sphere2 = sdSphere(spherePos2, 0.4 + cos(t * 2.5) * 0.1);

    float d = smin(vortex, sphere1, 0.3);
    d = smin(d, sphere2, 0.4);

    float ground = p.y + 2.0;
    return min(d, ground);
}

// Calculate normal at point p
vec3 getNormal(vec3 p) {
    float d = getSceneDistance(p);
    vec2 e = vec2(0.001, 0.0);
    vec3 n = d - vec3(
        getSceneDistance(p - e.xyy),
        getSceneDistance(p - e.yxy),
        getSceneDistance(p - e.yyx)
    );
    return normalize(n);
}

// Ray marching
float rayMarch(vec3 ro, vec3 rd) {
    float dO = 0.0;
    for(int i = 0; i < MAX_STEPS; i++) {
        vec3 p = ro + rd * dO;
        float dS = getSceneDistance(p);
        dO += dS;
        if(dO > MAX_DIST || dS < SURF_DIST) break;
    }
    return dO;
}

// Soft shadow
float softShadow(vec3 ro, vec3 rd, float mint, float maxt, float k) {
    float res = 1.0;
    float t = mint;
    for(int i = 0; i < 16; i++) {
        if(t < maxt) {
            float h = getSceneDistance(ro + rd * t);
            if(h < 0.001) return 0.0;
            res = min(res, k * h / t);
            t += h;
        }
    }
    return res;
}

// Ambient occlusion
float calcAO(vec3 pos, vec3 nor) {
    float occ = 0.0;
    float sca = 1.0;
    for(int i = 0; i < 5; i++) {
        float h = 0.01 + 0.12 * float(i) / 4.0;
        float d = getSceneDistance(pos + h * nor);
        occ += (h - d) * sca;
        sca *= 0.95;
    }
    return clamp(1.0 - 3.0 * occ, 0.0, 1.0);
}

// Background color with stars
vec3 getBackground(vec3 rd) {
    float y = rd.y * 0.5 + 0.5;
    vec3 topColor = vec3(0.1, 0.12, 0.2);
    vec3 bottomColor = vec3(0.05, 0.06, 0.1);
    vec3 bgColor = mix(bottomColor, topColor, y);

    vec3 stars = vec3(0.0);
    for(int i = 0; i < 3; i++) {
        float t = u_time * (0.1 + float(i) * 0.05);
        vec3 p = vec3(rd.x * 13.0 + t, rd.y * 17.0 - t, rd.z * 11.0);
        p = fract(p) - 0.5;
        float stars1 = 1.0 / (40.0 * length(p));
        stars += vec3(stars1) * (0.5 + 0.5 * sin(vec3(0.2, 0.5, 0.9) * u_time * 0.3));
    }

    return bgColor + stars * (rd.y * 0.5 + 0.5);
}

// Lighting calculation
vec3 getLight(vec3 p, vec3 rd) {
    vec3 lightPos = vec3(2.0, 5.0, 3.0);
    vec3 lightColor = vec3(1.0, 0.9, 0.8);
    vec3 secondLightPos = vec3(-3.0, 4.0, -2.0);
    vec3 secondLightColor = vec3(0.3, 0.5, 0.9);

    vec3 n = getNormal(p);
    vec3 baseColor = vec3(0.5) + 0.5 * cos(vec3(0.0, 0.33, 0.67) * PI * 2.0 + length(p) * 0.7 + u_time * 0.3);
    float metallic = 0.5 + 0.5 * sin(p.x * 2.0 + p.y * 3.0 + p.z * 1.0 + u_time * 0.2);
    float roughness = 0.1 + 0.3 * cos(p.y * 5.0 + u_time * 0.1);

    vec3 l1 = normalize(lightPos - p);
    float dif1 = clamp(dot(n, l1), 0.0, 1.0);
    float dist1 = length(lightPos - p);
    float atten1 = 1.0 / (1.0 + dist1 * dist1 * 0.05);

    vec3 l2 = normalize(secondLightPos - p);
    float dif2 = clamp(dot(n, l2), 0.0, 1.0);
    float dist2 = length(secondLightPos - p);
    float atten2 = 1.0 / (1.0 + dist2 * dist2 * 0.1);

    float shadow1 = softShadow(p, l1, 0.1, 10.0, 8.0);
    float shadow2 = softShadow(p, l2, 0.1, 5.0, 8.0);

    float ao = calcAO(p, n);

    vec3 reflected = reflect(rd, n);
    vec3 reflColor = getBackground(reflected);

    vec3 h1 = normalize(l1 - rd);
    float spec1 = pow(clamp(dot(n, h1), 0.0, 1.0), 16.0);

    vec3 h2 = normalize(l2 - rd);
    float spec2 = pow(clamp(dot(n, h2), 0.0, 1.0), 8.0);

    vec3 ambient = vec3(0.1, 0.11, 0.13) * ao;
    vec3 diffuse1 = baseColor * lightColor * dif1 * atten1 * shadow1;
    vec3 diffuse2 = baseColor * secondLightColor * dif2 * atten2 * shadow2;
    vec3 specular1 = lightColor * spec1 * (1.0 - roughness) * shadow1;
    vec3 specular2 = secondLightColor * spec2 * (1.0 - roughness) * shadow2;
    vec3 reflection = reflColor * metallic;

    return ambient + diffuse1 + diffuse2 + specular1 + specular2 + reflection;
}

void main() {
    // Normalize pixel coordinates (from 0 to 1)
    vec2 uv = (gl_FragCoord.xy - 0.5 * u_resolution) / min(u_resolution.x, u_resolution.y);

    // Camera setup
    float camTime = u_time * 0.3;
    float camDist = 6.0;
    vec3 ro = vec3(camDist * sin(camTime), 2.0 + sin(camTime * 0.5), camDist * cos(camTime));
    vec3 lookAt = vec3(0.0, 0.0, 0.0);
    vec3 forward = normalize(lookAt - ro);
    vec3 right = normalize(cross(vec3(0.0, 1.0, 0.0), forward));
    vec3 up = cross(forward, right);

    // Compute ray direction
    vec3 rd = normalize(uv.x * right + uv.y * up + forward);

    // Ray marching
    float dist = rayMarch(ro, rd);
    vec3 p = ro + rd * dist;

    // Determine color based on intersection
    vec3 color;
    if (dist < MAX_DIST) {
        color = getLight(p, rd);
    } else {
        color = getBackground(rd);
    }

    // Output to screen
    gl_FragColor = vec4(color, 1.0);
}

vert:
attribute vec2 a_position;
void main() {
  gl_Position = vec4(a_position, 0., 1.);
}

frag:
precision highp float;

uniform float u_time;
uniform vec2 u_resolution;

//–– ray-marcher constants ––//
#define MAX_STEPS 100
#define MAX_DIST 100.0
#define SURF_DIST 0.001
#define PI 3.14159265

//–– Superformula parameters ––//
const float m1 = 6.0;
const float n11 = 0.2;
const float n12 = 1.7;
const float n13 = 1.7;

const float m2 = 7.0;
const float n21 = 0.2;
const float n22 = 1.7;
const float n23 = 1.7;

//–– Superformula 2D helper ––//
float sf2D(float x, float m, float n1, float n2, float n3, float a, float b) {
    float t    = 0.5 * m * x;
    float part = pow(abs(cos(t)/a), n2) + pow(abs(sin(t)/b), n3);
    return pow(part, -1.0 / n1);
}

//–– Compute the 3D Superformula radius at (θ, φ) ––//
float superformula(float theta, float phi) {
    float a = 1.0;
    float b = 1.0;
    float r1 = sf2D(theta, m1, n11, n12, n13, a, b);
    float r2 = sf2D(phi,   m2, n21, n22, n23, a, b);
    return r1 * r2;
}

//–– SDF for a superformula shell ––//
float sdSuperformula(vec3 p) {
    float r     = length(p);
    float theta = atan(p.y, p.x);
    float phi   = acos(clamp(p.z / r, -1.0, 1.0));
    return r - superformula(theta, phi);
}

//–– Smooth union ––//
float smin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5*(b - a)/k, 0.0, 1.0);
    return mix(b, a, h) - k*h*(1.0 - h);
}

//–– Scene SDF ––//
float getSceneDistance(vec3 p) {
    // slowly rotate
    float ang = u_time * 0.3;
    mat3 rotY = mat3(
        cos(ang), 0, sin(ang),
        0,        1, 0,
       -sin(ang), 0, cos(ang)
    );
    p = rotY * p;

    float dSup    = sdSuperformula(p);
    float dGround = p.y + 1.5;
    return smin(dSup, dGround, 0.5);
}

//–– Numerical normal ––//
vec3 getNormal(vec3 p) {
    vec2 e = vec2(0.001, 0.0);
    return normalize(vec3(
        getSceneDistance(p + e.xyy) - getSceneDistance(p - e.xyy),
        getSceneDistance(p + e.yxy) - getSceneDistance(p - e.yxy),
        getSceneDistance(p + e.yyx) - getSceneDistance(p - e.yyx)
    ));
}

//–– Ray marcher ––//
float rayMarch(vec3 ro, vec3 rd) {
    float dO = 0.0;
    for(int i = 0; i < MAX_STEPS; i++) {
        vec3 p = ro + rd * dO;
        float dS = getSceneDistance(p);
        dO += dS;
        if(dO > MAX_DIST || dS < SURF_DIST) break;
    }
    return dO;
}

//–– Simple soft shadow ––//
float softShadow(vec3 ro, vec3 rd) {
    float res = 1.0, t = SURF_DIST;
    for(int i=0; i<16; i++) {
        float h = getSceneDistance(ro + rd * t);
        if(h < 0.001) return 0.0;
        res = min(res, 16.0 * h / t);
        t += clamp(h, 0.01, 0.1);
    }
    return res;
}

//–– Ambient occlusion ––//
float calcAO(vec3 pos, vec3 nor) {
    float occ = 0.0, sca = 1.0;
    for(int i=0; i<5; i++){
        float h = 0.01 + 0.12 * float(i)/4.0;
        float d = getSceneDistance(pos + nor * h);
        occ += (h - d) * sca;
        sca *= 0.95;
    }
    return clamp(1.0 - 3.0 * occ, 0.0, 1.0);
}

//–– Background gradient + stars ––//
vec3 getBackground(vec3 rd) {
    float t = rd.y * 0.5 + 0.5;
    vec3 col = mix(vec3(0.05,0.06,0.1), vec3(0.1,0.12,0.2), t);
    // tiny sparkle
    float star = 1.0 / (40.0 * length(fract(rd*20.0) - 0.5));
    return col + star * t;
}

//–– Lighting + shading ––//
vec3 getLight(vec3 p, vec3 rd) {
    vec3 n       = getNormal(p);
    vec3 lightP  = vec3(2.0, 5.0, 3.0);
    vec3 ld      = normalize(lightP - p);
    float dif    = clamp(dot(n, ld), 0.0, 1.0);
    float shad   = softShadow(p + n * SURF_DIST * 2.0, ld);
    float ao     = calcAO(p, n);
    vec3 baseCol = vec3(0.6, 0.4, 0.8);
    vec3 difCol  = baseCol * dif * shad;
    vec3 ambCol  = vec3(0.1) * ao;
    return ambCol + difCol;
}

void main() {
    // uv coords
    vec2 uv = (gl_FragCoord.xy - 0.5*u_resolution) 
              / min(u_resolution.x, u_resolution.y);

    // camera
    float t = u_time * 0.2;
    vec3 ro = vec3(6.0*sin(t), 2.0, 6.0*cos(t));
    vec3 fw = normalize(-ro);
    vec3 rt = normalize(cross(vec3(0,1,0), fw));
    vec3 up = cross(fw, rt);
    vec3 rd = normalize(uv.x*rt + uv.y*up + fw);

    float d = rayMarch(ro, rd);
    vec3 col;
    if(d < MAX_DIST) {
        vec3 p = ro + rd * d;
        col   = getLight(p, rd);
    } else {
        col   = getBackground(rd);
    }

    gl_FragColor = vec4(col, 1.0);
}
